void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ch = .5/iChannelResolution[1].xy;
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(1.0);
    for(float i=-1.; i<2.; i++){
        for(float j=-1.; j<2.; j++){
            fragColor *= vec4(texture(iChannel0, uv+vec2(i, j)*ch));
        }
    }
    fragColor = vec4(pow(fragColor.x,.1), pow(fragColor.y,.1), pow(fragColor.z,.1), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ch = 1./iChannelResolution[0].xy;
    vec2 uv = fragCoord/iResolution.xy;
    vec3 v1 = texture(iChannel0,uv).rgb - texture(iChannel0,uv+vec2(ch.x,0.)).rgb;
    vec3 v2 = texture(iChannel0,uv).rgb - texture(iChannel0,uv+vec2(-ch.x,0.)).rgb;
    vec3 v3 = texture(iChannel0,uv).rgb - texture(iChannel0,uv+vec2(0.,ch.y)).rgb;
    vec3 v4 = texture(iChannel0,uv).rgb - texture(iChannel0,uv+vec2(0.,-ch.y)).rgb;
    vec3 v5 = texture(iChannel0,uv).rgb - texture(iChannel0,uv+vec2(ch.x,ch.y)).rgb;
    vec3 v6 = texture(iChannel0,uv).rgb - texture(iChannel0,uv+vec2(-ch.x,ch.y)).rgb;
    vec3 v7 = texture(iChannel0,uv).rgb - texture(iChannel0,uv+vec2(ch.x,-ch.y)).rgb;
    vec3 v8 = texture(iChannel0,uv).rgb - texture(iChannel0,uv+vec2(-ch.x,-ch.y)).rgb;
    vec3 dev = v1*v1+v2*v2+v3*v3+v4*v4+v5*v5+v6*v6+v7*v7+v8*v8;
    float edge = dev.x+dev.y*dev.z*50.;
    fragColor = vec4(vec3(edge),1.0);
}
