/*
*算法来自：https://blog.csdn.net/tjw02241035621611/article/details/80135576
*algorithm from https://blog.csdn.net/tjw02241035621611/article/details/80135576
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 ruv = uv / vec2(iResolution.x/iResolution.y,1.0);//原始uv
    //宽高比正常uv
    uv = (ruv * 2.0 - 1.0) *  vec2(iResolution.x/iResolution.y,1.0);
    vec2 st =  uv * vec2(.5+(ruv.y+1.0)*0.5, .03)+vec2(iTime*.2-ruv.y*.1, iTime*.2);
    //拉伸实现
    float f = texture(iChannel0, st).y * texture(iChannel0, st*.773).x * 1.55;
    f = clamp(pow(abs(f), 23.0) * 13.0, 0.0, (ruv.y-.2)*.14);
    vec3 col;
    col += vec3(f,f,f); 

    // Output to screen
    fragColor = vec4(col,1.0);
}
