#define DIVS 20.0
#define PI 3.141592759


float rand (vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

vec2 rotate (vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

bool approx (float a, float b, float d)
{
    return abs(a-b)<=d;
}

float circleSquare (vec2 uv, float t)
{
    float power = 4./(cos(t*PI/8.)+1.);
    float lhs =
        pow(abs(uv.x-uv.y), power) +
        pow(abs(uv.y+uv.x), power)
    ;
    float rhs = 1.;

    return smoothstep(0.4, 0.5, rhs - lhs);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x*= iResolution.x / iResolution.y;

    // grid
    vec2 div = uv * DIVS;
    vec2 sq = fract(div) - .5;
    vec2 id = floor(div);

    // rotation
    float rot = clamp(cos(iTime + id.x *.2 + id.y * .2), -.5, .5) + .5;
    sq = rotate(sq, rot * PI * .25);

    // rectangles
    vec2 asq = abs(sq);
	float rect = circleSquare(asq * 2., iTime * 2. + id.x);


    // we give a background in diagonal
    vec2 idc = id / DIVS;
    vec3 col = vec3(0);
    col = mix(vec3(.9, 0, .1), vec3(0.5, 0.5, 0.8), min(1.0, idc.x - idc.y + .5)) * rect;
    col-= .5 - length((id+.5) / DIVS);

    // color is made by having circles running in... circles
    vec2 ptc1 = vec2(cos(iTime*.5) * .4, sin(iTime*.5) * .4);
    col.rgb+= vec3(0, 1., .5) * (1. - length(ptc1 - idc)) * (1. - rect);

    vec2 ptc2 = vec2(cos(iTime*.5 + PI) * .4, sin(iTime*.5 + PI) * .4);
    col.rgb+= vec3(.5, 0., .8) * (1. - length(ptc2 - idc)) * (1. - rect);
    vec2 ptc3 = vec2(cos(iTime*1.0 + PI*.5) * .4, sin(iTime*1.0 + PI*.5) * .4);
    col.rgb+= vec3(1., 1., 1.) * (1. - length(ptc3 - idc)) * (1. - rect) * (cos(iTime)*.2 + .4);

    col*= rand(uv) * .1 + .9;


    // Time varying pixel color
    //col = mix(col, vec3(1), rect);


    // we square the view
    float visible = step(abs(uv.x), .5);
    visible*= step(abs(id.x), DIVS*.5 - 3.);
    visible*= step(abs(id.y+.5), DIVS*.5 - 1.);
    col*= visible;

    id = floor(div * 2. - .5);
    id.y = floor(div.y * 2.);
    visible = step(abs(id.x), DIVS - 5.);
    visible*= step(abs(id.y + .5), DIVS - 1.5);

    col+= rand(uv) * .1;

    // the background
    col+= (1. - visible) * clamp(pow(max(0., (1.-length(uv))), .2)* 1.1, .2, 1.);
    col.b*= .92;

    // Output to screen
    fragColor = vec4(col,1.0);
}
