vec4 blur(in vec2 p){
    #define bcomp x
    vec3 e = 2.*vec3(1, 0, -1);
    vec2 px = iResolution.xy/iResolution.xy;
	vec4 res = vec4(0.0);
	res += T(p + e.xx*px ) + T(p + e.xz*px ) + T(p + e.zx*px ) + T(p + e.zz*px );
    res += (T(p + e.xy*px ) + T(p + e.yx*px ) + T(p + e.yz*px ) + T(p + e.zy*px ))*2.;
	res += T(p + e.yy*px )*4.;
    return res/16.;
}

