float tau = atan(1.0) * 8.0;

mat2 genRotMat(float val){
	return mat2(cos(val),-sin(val),sin(val),cos(val));
}

vec3 opAngRep( vec3 p, float a )
{
	vec2 polar = vec2(atan(p.y, p.x), length(p.xy));
    polar.x = mod(polar.x + a / 2.0, a) - a / 2.0;

    return vec3(polar.y * vec2(cos(polar.x),sin(polar.x)), p.z);
}




float RepN2(vec3 p, float numItem)
{
    vec3 pp=p;
    //p.xy *= genRotMat(iTime);/// genera la rotacion del engrane 
    vec3 rep2 = opAngRep(p, tau / numItem);

    float sdb1= sdCylinderYZ(rep2.zyx-vec3(10.0,0.0,5.0), vec2(1.5,20.0) );
    float sdb2= sdBox( p-vec3(0.0,0.0,-4.0), vec3(10.0,10.0,1.0) );
    float sdb3= sdBox( p-vec3(0.0,0.0,28.0), vec3(10.0,10.0,1.0) );

    float res = unionSDF( sdb1,sdb2);
    res = unionSDF( res,sdb3);
    return  res;
}



