//tutorial https://www.youtube.com/watch?v=dXyPOLf2MbU

#define S(a,b,t) smoothstep(a,b,t)
#define HEART_COLOR vec3(1.,0.02,0.02)

float smax(float a, float b, float k){

    float h = clamp((b-a)/k+.5,0.,1.);
    return mix(a,b,h)+h*(1.-h)*k*.5;
}


float Heart(vec2 uv, float b, float r){

    b*=r;

    uv.x*=1.;
    uv.y-=smax(sqrt(abs(uv.x))*.4,b,.1);
    uv.y+=.1*r;

    float d = length(uv);
    return S(r+b,r-b-0.01,d);

}
mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(20.,2006.)))*
        400008.5453123);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m= iMouse.xy/iResolution.xy;
    vec3 col = vec3(0);

    uv*=10.;

    vec2 f =fract(uv)*3.-1.5;
    vec2 id = floor(uv);
    f*=rotate2d(abs(sin(iTime*0.5))*3.14*(id.x+id.y+0.08)*2.);
    f *= scale( vec2(abs(cos(iTime*0.5)+.2)) ) ;

float c= Heart(f,m.y,0.5*abs(sin(iTime*0.5)));
    col = vec3(c*HEART_COLOR*(random(id)+0.5));



    fragColor = vec4(col,1.0);
}
