#define pi (acos(-1.))

const vec3 color = vec3(.1,.7,.6);

vec2 rotate(vec2 a, float b)
{
    float c = cos(b);
    float s = sin(b);
    return vec2(
        a.x * c - a.y * s,
        a.x * s + a.y * c
    );
}

float scene(vec3 p)
{
    return (max(
	    abs(p.x+p.y)-p.z,
	    abs(p.x-p.y)+p.z
	)-1.)/sqrt(3.);

