vec3 trace(vec3 cam, vec3 dir)
{
    float t;
    for(int i = 0; i < 100; ++i)
    {
        float k = scene(cam+dir*t);
        t += k;
        if (k < .001)
        {
            vec3 h = cam+dir*t;
    		vec2 o = vec2(.001, 0);
    		vec3 n = normalize(vec3(
        		scene(h+o.xyy)-scene(h-o.xyy),
        		scene(h+o.yxy)-scene(h-o.yxy),
        		scene(h+o.yyx)-scene(h-o.yyx)
    		));

			if (iMouse.z > 0.)
    		{
        		return n*.5+.5;
    		}
    		else
    		{
		        const vec3 lightdir = normalize(vec3(1,5,2));

		        float light = dot(n,lightdir)*.5+.5;

		        return light * color;
    		}
        }
    }
    return vec3(1);
}

void cameraspin(inout vec3 p)
{
    p.yz = rotate(p.yz, .3);
    p.xz = rotate(p.xz, iTime*.1);
}

void mainImage(out vec4 out_color, vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy - .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 cam = vec3(0,0,-15.);
    vec3 dir = normalize(vec3(uv,4));

    cameraspin(cam);
    cameraspin(dir);

    out_color.rgb = trace(cam,dir);
    if (iMouse.z <= 0.)
    {
    	out_color.rgb *= pow(color, vec3(2.*dot(uv,uv)));
    	out_color.rgb = pow(out_color.rgb,vec3(.45));
    }
}
