#define Diffusion 0
#define ClassicalWave 1
#define Schrodinger 2

#define EQ Schrodinger

#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
vec2 x (vec2 a, vec2 b) {return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A (U);  // Q = wave function
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0)),
        v = 0.25*(n+e+s+w)-Q; // v = Laplacian
    float dt = 1.;
    if (EQ==0) {
// Diffusion Equation :
    	Q.x = dt*v.x + Q.x;

    } else if (EQ == 1) {
// Classical Wave Equation :
        Q.xy = dt*v.x*vec2(1,1) + Q.xy*mat2(1,1,0,1);

    } else {
// Schrodinger Equation :
        Q.xy = dt*v.x*vec2(1,0) + Q.xy*mat2(1,1,-1,0);
    }


// Mouse + init + Boundary:  
    vec2 M = (U-iMouse.xy)/10.;
    vec2 m = (iMouse.xy-iMouse.zw)/10.;
    if (iMouse.z > 0. ) Q.xy = mix(Q.xy,vec2(cos(m.x*M.x+m.y*M.y),sin(m.x*M.x+m.y*M.y)),exp(-.3*dot(M,M)));
    if (U.x < 1. || U.y < 1. || R.x-U.x<1. || R.y-U.y<1.) Q.xy *= 0.1;
    if (iFrame < 1) {
        vec2 D = (U-0.5*R)/10.;
		Q.xy = 3.*exp(-dot(D,D))*(vec2(cos(-9.*D.x+D.y),sin(-9.*D.x+D.y)));
    }
}
