// Based on "Reflecting Heart" (mouse stops motion)

float Maxv3 (vec3 p);
vec2 Rot2D (vec2 q, float a);
float Fbm2 (vec2 p);

vec3 sunDir;
float tCur, dstFar, a, b;
const float pi = 3.14159;

float SurfDf (vec3 p)
{
// f(x,y,z) = (x^2 + ((1 + b) * y)^2 + z^2 - 1)^3 - x^2 * z^3 - a * y^2 * z^3
  float v1, v2;
  v1 = (1. + b) * p.y;
  v2 = p.x * p.x + v1 * v1 + p.z * p.z - 1.;
  return v2 * v2 * v2 - (p.x * p.x + a * p.y * p.y) * p.z * p.z * p.z;
}

vec3 SurfNf (vec3 p)
{
  float v1, v2;
  v1 = (1. + b) * p.y;
  v2 = p.x * p.x + v1 * v1 + p.z * p.z - 1.;
  return normalize (vec3 (6. * p.x * v2 * v2 - 2. * p.x * p.z * p.z * p.z,
     6. * (1. + b) * v1 * v2 * v2 - 2. * a * p.y * p.z * p.z * p.z,
     6. * p.z * v2 * v2 - 3. * (p.x * p.x + a * p.y * p.y) * p.z * p.z));
}

vec2 BallHit (vec3 ro, vec3 rd, float s)
{
  float dbIn, dbOut, b, d;
  b = dot (rd, ro);
  d = b * b + s * s - dot (ro, ro);
  dbIn = dstFar;
  dbOut = dstFar;
  if (d > 0.) {
    d = sqrt (d);
    dbIn = - b - d;
    dbOut = - b + d;
  }
  return vec2 (dbIn, dbOut);
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec2 bnd;
  float dHit, h, s, ds, sLo, sHi;
  const float nDiv = 200.;
  dHit = dstFar;
  bnd = BallHit (ro, rd, 2.);
  if (bnd.x < dstFar) {
    s = bnd.x;
    sLo = bnd.x;
    ds = (bnd.y - bnd.x) / nDiv;
    for (float j = 0.; j < nDiv; j ++) {
      h = SurfDf (ro + s * rd);
      if (h < 0. || s > bnd.y) break;
      sLo = s;
      s += ds;
    }
    if (h < 0.) {
      sHi = s;
      for (float j = 0.; j < 16.; j ++) {
        s = 0.5 * (sLo + sHi);
        if (SurfDf (ro + s * rd) > 0.) sLo = s;
        else sHi = s;
      }
      dHit = 0.5 * (sLo + sHi);
    }
  }
  return dHit;
}

vec3 StarPat (vec3 rd)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 1031.13 * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 1071.13 * q.yx));
  return 8. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, rdo, vn;
  float dstObj, sd, sFac;
  a = 0.5;
  b = 0.5;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = SurfNf (ro);
    rdo = rd;
    rd = reflect (rd, vn);
    sFac = 2.;
  } else {
    sFac = 1.;
  }
  sd = pow (max (dot (rd, sunDir), 0.), 32.);
  sd = (0.1 * sd + 0.4 * pow (sd, 16.));
  col = mix (sFac * StarPat (rd), vec3 (1., 0.7, 0.) * sd, min (1., 2. * sd));
  if (dstObj < dstFar) {
    col += vec3 (0.07) * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) + vec3 (0.05, 0.03, 0.03) +
       vec3 (0.05, 0., 0.) * (0.6 + 0.4 * sin (pi * tCur)) * max (dot (- vn, rdo), 0.);
  } else col += vec3 (0.03, 0.03, 0.);
  return clamp (col, 0., 1.);
}

#define AA  1

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = pi;
  el = 0.6 * pi;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el -= pi * mPtr.y;
  } else {
    az -= 0.01 * pi * tCur;
    el += 0.1 * pi * sin (0.021 * pi * tCur);
  }
  ro = vec3 (0., 0.05, -4.);
  ro.yz = Rot2D (ro.yz, el);
  ro.xy = Rot2D (ro.xy, az);
  dstFar = 20.;
  sunDir = normalize (vec3 (1., 1., -0.2));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), 2.));
    rd.yz = Rot2D (rd.yz, el);
    rd.xy = Rot2D (rd.xy, az);
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}
