//This parameter changes the magnification.
const float mag = 9.0;
const float distortion_scale = mag*mag;

const int GREEN = 0;
const int BLUE = 1;
const int GRAY = 2;
const int YELLOW = 3;
const int WHITE = 4;
const int FOREST_GREEN = 5;
const int LIGHTBLUE = 6;
const int SKYBLUE = 7;
const int SNOW = 8;
const int WHITESMOKE = 9;
const int LIGHTGRAY = 10;
const int LIME = 11;
const int LIGHTYELLOW = 12;
const int BEIGE = 13;
const int SAND = 14;
const int TAN = 15;
const int ORANGE = 16;
const int RED = 17;
const int BLACK = 18;
const int DARKGRAY = 19;
const int ORANGERED = 20;

const int SWAMP_BIOME = 1;
const int GLACIER_BIOME = 2;
const int FORESTS_AND_MOUNTAINS = 3;
const int SAVANNA_BIOME = 4;
const int FOREST_BIOME = 5;

//this function is from https://www.shadertoy.com/view/4djSRW
int hash12(vec2 p,float num_colors)
{
    //set the number of colors to be randomly generated

    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return int(floor(fract((p3.x + p3.y) * p3.z)*num_colors));
}

int magnify(vec2 fragCoord,float mag,float num_colors){
    fragCoord += vec2(sin(fragCoord.y/(distortion_scale*mag)+iTime),sin(fragCoord.x/(distortion_scale*mag)+iTime))*10.0*mag*mag;
    return hash12(floor(fragCoord/pow(3.0,mag)),num_colors);
}

ivec4 get_neighbors(vec2 fragCoord,float mag,float colors){
	return ivec4(magnify(fragCoord+vec2(0,1),mag,colors),
        magnify(fragCoord+vec2(1,0),mag,colors),
        magnify(fragCoord-vec2(0,1),mag,colors),
        magnify(fragCoord-vec2(1,0),mag,colors));
}


bool is_next_to(int color,ivec4 neighbors){
	return (neighbors.x == color)
        || (neighbors.y == color)
        || (neighbors.z == color)
        || (neighbors.w == color);
}

vec3 rgb(float r,float g,float b){
	return vec3(r,g,b)/255.0;
}

vec3 get_color(int color){
    if(color == BLUE){
    	return vec3(0.149,0.141,0.912);
   	}
    else if(color == GREEN){
    	return vec3(0.000,0.833,0.224);
   	}
    else if(color == FOREST_GREEN){
    	return rgb(34.0,139.0,34.0);
   	}
    else if(color == WHITE){
    	return vec3(1.0,1.0,1.0);
   	}
    else if(color == GRAY){
    	return vec3(192.0,192.0,192.0)/255.0;
    }
    else if(color == YELLOW){
    	return vec3(1.0,1.0,0.0);
   	}
    else if(color == LIGHTBLUE){
    	return rgb(173.0,216.0,230.0);
   	}
    else if(color == SKYBLUE){
    	return rgb(135.0,206.0,235.0);
    }
    else if(color == SNOW){
    	return rgb(255.0,250.0,250.0);
    }
    else if(color == WHITESMOKE){
    	return rgb(245.0,245.0,245.0);
    }
    else if(color == LIGHTGRAY){
    	return rgb(211.0,211.0,211.0);
    }
    else if(color == LIME){
    	return rgb(0.0,255.0,0.0);
    }
    else if(color == LIGHTYELLOW){
    	return rgb(255.0,255.0,153.0);
    }
    else if(color == BEIGE){
    	return rgb(245.0,245.0,220.0);
    }
    else if(color == TAN){
    	return rgb(210.,180.,140.);
    }
    else if(color == ORANGE){
    	return vec3(255,127,80)/255.0;
    }
    else if(color == RED){
    	return vec3(1.0,0.0,0.0);
   	}
    else if(color == DARKGRAY){
    	return rgb(128.0,128.0,128.0);
    }
    else if(color == ORANGERED){
    	return rgb(255.0,69.0,0.0);
    }
}


int glacier_biome(inout int color1, ivec4 neighbors){
	int GREEN = 0;
    int BLUE = 1;
    int GRAY = 2;
    int WHITE = 3;
    int YELLOW = 4;

    return (color1 == GREEN && is_next_to(BLUE,neighbors))
        ? GRAY
    : (color1 == GREEN && is_next_to(WHITE,neighbors))
        ? GRAY
    : (color1 == BLUE && is_next_to(GREEN,neighbors))
        ? WHITE
    : (color1 == YELLOW && is_next_to(GREEN,neighbors))
        ? GRAY
    : (color1 == YELLOW && is_next_to(GRAY,neighbors))
        ? BLUE
    :(color1 == WHITE && is_next_to(YELLOW,neighbors))
        ? GRAY
    :(color1 == YELLOW && is_next_to(WHITE,neighbors))
        ? WHITE
    :(color1 == GRAY && is_next_to(BLUE,neighbors))
        ? BLUE
    :color1;
}

void biome(inout int color1, ivec4 neighbors,int biome){
    color1 =
        glacier_biome(color1,neighbors);
}

int biome(in vec2 fragCoord,float mag,float zoom,int colors[5],int biome1){
    fragCoord *= zoom;


    float num_colors = float(colors.length());

    int color1 = magnify(fragCoord,mag,num_colors);

    ivec4 neighbors;

    while(mag > 1.0){
                float avg =(100.0*(mag/float(color1)));
        fragCoord += vec2(sin(fragCoord.y/avg)*10.0,sin(fragCoord.x/avg)*10.0)*mag*mag;

    neighbors = get_neighbors(fragCoord,mag,num_colors);
    biome(color1, neighbors,biome1);
    mag -= 1.0;
    }
    return colors[int(color1)];;
}


int glacier_biome(in vec2 fragCoord,float mag,float zoom,int[5] colors){
    return biome(fragCoord,mag,zoom,colors,GLACIER_BIOME);
}

int lava_biome(in vec2 fragCoord,float mag,float zoom){
	return glacier_biome(fragCoord,mag,zoom,int[](DARKGRAY,YELLOW,ORANGERED,ORANGE,ORANGERED));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = mag*(((iMouse.x-iMouse.y)*2.0+iResolution.x)/iResolution.x);
    fragCoord *= zoom;
    fragCoord = fragCoord + floor(iTime*100.0)*vec2(zoom,0.0);

	int biome = lava_biome(fragCoord,mag,1.0);

    fragColor = vec4(get_color(biome),1.0);
}
