float hash(vec2 p) {
    p = fract(p*vec2(222.910,317.54));
    p += dot(p.yx,p+325.21);
    return fract(p.x * p.y);
}
mat2 r(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}

float shape(vec2 uv,float factor) {
    return smoothstep(0.551,0.5,sin(floor(factor*iTime*8.)+length(log(1.+abs(uv)*100.))));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    if( .5+abs(uv.y ) > .8 ) uv = fract(uv*10.+vec2(sign(uv.y)*iTime,0.))-.5;
    uv*=r(floor(iTime*4.));
   float d = mix(shape(uv,1.),shape(uv,-1.1),0.5);
    vec3 col =  vec3(d*vec3(.5+cos(floor(iTime)*2.)/2.,hash(floor(uv))*.3+sin(floor(-iTime)*4.)/2.,.4+sin(floor(iTime)*8.)/2.));
    fragColor = vec4(col,1.0);
}
