#define pi 3.1415926

float wave(float t)
{
    return sin(2.0 * pi * t);
}

float saw(float t)
{
    return 2.0 * fract(t) - 1.0;
}

#define S 8.0

ivec2 note(float time)
{
    int n = int(floor(time * S)); // keep in sync with local
    return ivec2(base[n % N] - 24, voice[n % N]);
}

float local(float time)
{
    return fract(time * S) / S; // keep in sync with note
}

float sound1(float freq, float time)
{
    float t = freq * time;
    float h = clamp(1.0 - 4.0 * time, 0.0, 1.0); // linear
    return mix(wave(t), saw(t), h) * h; // plucked string like sound
}

float sound2(float freq, float time)
{
    float t = freq * time;
    float h = 0.5;
    return mix(wave(t), saw(t), h);
}


vec2 bassSynth(float time, float tempo)
{	


     ivec2 n = note(time);
    float t = local(time);
    vec2 f = 0.5 * 440.0 * exp2(vec2(n) / 12.0);


    float s1= sound2(f.y, t)*2.0;
    return vec2(s1);
}


vec2 leadSynth(float time, float tempo) 
{


    ivec2 n = note(time);
    float t = local(time);
    vec2 f = 0.5 * 440.0 * exp2(vec2(n) / 12.0);
    //vec2 f = 0.5 *  exp2(vec2(n) / 12.0);

    float s1= sound1(f.x, t)*2.0;
    return vec2(s1);
}

vec2 kickSynth(float time, float tempo) {
    const float dist = 3.0;
    tempo = mod(tempo,1.0); 
    return vec2(clamp(sin(pow((tempo+0.2)*0.0001,-0.5))*exp(-3.0*tempo),-(1.0/dist),(1.0/dist)) * dist);
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 clapSynth(float time, float tempo) {
    float q = floor(mod(tempo*2.0,32.0)); //quaver number;
    float b = floor(q/8.0);//bar number
    float qb = mod(q,8.0); //quaver in bar;
    float ampMask = float((qb >= 2.0 && qb < 4.0) || (qb >= 6.0 && qb < 7.0)
                         || (b == 3.0 && (qb == 1.0 || qb >= 5.0))); 
    tempo = mod(tempo*4.0 ,2.0);
    return vec2(rand(vec2(time+1.2,tempo+6.1)) * ampMask * exp(-1.5*tempo),
                rand(vec2(time+2.1,tempo+1.6)) * ampMask * exp(-1.5*tempo));
}

vec2 mainSound( in int samp,float time)
{
    float t = time;
    float tempo = t * (bpm/60.0);

    vec2 bass = bassSynth(t,tempo) * 0.30; //mixing here
    vec2 lead = leadSynth(t*1.005,tempo) * 0.333;
    vec2 kick = kickSynth(t,tempo) * 0.333;
    vec2 clap = clapSynth(t,tempo) * 0.333;

    vec2 synths = bass + lead; //sidechain


    synths *= min(mod(tempo,1.0)+0.4,1.0);

    return synths + kick + clap;
}
