// Generic (sexy, seamless & slightly wibbly) neonbars with extraglow etc. for SST - Del 14/10/2019

vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}

vec3 SSTLines(vec2 duv, vec3 col2, float nl, float grad,float thickness, float wibblesize, float wibblespeed, float glowintensity, float glowclamp, float extraglow)
{
    if (iMouse.z<0.5)
    	col2 = hsv2rgb(vec3(duv.y*0.1+duv.x*0.1+iTime*0.025,0.5,0.5));


    duv.y -= (floor(duv.x)*grad) + (duv.x*grad);
    duv = fract(duv);
    float l1 = abs(fract((duv.x*grad-duv.y)*nl) -0.5);
    float dd = sin(iTime*wibblespeed+duv.x*6.28)*wibblesize;
    l1 = min(glowclamp, (thickness+dd)/l1);
    vec3 col = col2*l1*glowintensity+(dd*extraglow);
    return mix(col2,col,l1);
}



void mainImage( out vec4 O, in vec2 fragCoord )
{
   	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    uv.x -= 1.0;
    uv.x = dot(uv,uv);
    uv *= 1.0+sin(iTime)*0.25;	// zoom
    uv.y += iTime*0.1;			// vscroll

    // lots of silly parameters, have fun :)
    float grad = sin(iTime*0.35)*0.2;
    float nlines = 5.0;
    float thickness = 0.15;
    float wibblesize = 0.015;
    float glowintensity = 3.25;
    float wibblespeed = 6.5;
    float glowclamp = 1.0;
    float extraglow = 9.0;
    vec3 col = hsv2rgb(vec3(iTime*0.025,0.5,0.5));
	col = SSTLines(uv, col, nlines, grad, thickness, wibblesize, wibblespeed, glowintensity, glowclamp, extraglow);

	// vignette
   	vec2 q = fragCoord.xy / iResolution.xy;
    col *= 0.5 + 0.5*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.35 );


    O = vec4(col.xyz,1.0);
}
