/////////////////////////////////////////////////////////////

// Constants

float planetSize = 0.04;
vec3 planetCenter = vec3(0,0,-0.06);


/////////////////////////////////////////////////////////////


float map(vec3 p){
    //sphere
    vec3 q = p + planetCenter;

    // Map cartesian to spherical coordinate system
    // https://en.wikipedia.org/wiki/Spherical_coordinate_system#Cartesian_coordinates
    float r = length(q);
    float a = atan(q.y/q.x) + iTime*0.4*p.z;
    float b = atan(length(q.xy)/q.z)*0.15 + iTime*0.1;

    float d = texture(iChannel0,vec2(a,b)*2./3.141).x;
    return length(q) - planetSize + 0.01*d; 
} 


// Raytracer
float trace(vec3 origin, vec3 ray){ 
    float t = 0.; 
    for (int i = 0; i < 64; ++i){ 	
        vec3 p = origin + ray * t; 
        float d = map(p);
        t += d*0.3; 
    } return t; 
} 

vec3 normal(vec3 p){
	return vec3(0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 
    // Normalized pixel coordinates (from 0 to 1) 
    vec2 uv = fragCoord/iResolution.xy; 
    uv = uv * 2.0 - 1.0; 
    uv.x *= iResolution.x / iResolution.y; 

    // Start raytracer
    vec3 ray = normalize(vec3(uv, 1.0)); 
    float rotSpeed = 0.4;
    vec3 origin = vec3(0,0,0); 
    float t = trace(origin, ray); 

    vec3 surface = origin + ray * t;


    // Calculate fog
    float fog = 1.0 / (1.0 + t*t*400.); 
    vec3 fc = vec3(fog); 
    // Output to screen 
    fragColor = vec4(fc,1.0); 
}
