mat2 r2d(float a) {
	float c = cos(a), s = sin(a);
    return mat2(
        c, s,
        -s, c
    );
}

vec2 f(vec2 st, vec2 c) {
    vec2 stt = st;
    int i = 0;
    while (i<125 && dot(stt,stt)<(10.)) {
    	stt.x = 2.*st.x*st.y + iTime*.1;
        stt.y = st.y*st.y - st.x*st.x;
        stt = r2d(iTime*.1+stt.x*.1)*stt;
        st = stt;
        c+=8.*normalize(stt)*tan(stt)*cos(stt);
        i++;
    }
    return vec2(1.0)-c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 8.-0.02*iTime;
	uv -= vec2(7.0-0.014*iTime,3.9-0.005*iTime);

    // Output to screen
    vec2 c = f(uv,vec2(1.));
    fragColor = vec4(vec3(0.,c),1.0);
}
