#define INV_PI 0.318309886

float safe_atan2_pi(float y, float x) {
    return x == 0.0 ? 0.5*sign(y) : atan(y, x)*INV_PI;
}

vec4 sampleAt(vec2 fragCoord) {
    float t = iTime * 0.4;

    vec2 uv = (fragCoord - iResolution.xy*.5) / iResolution.y;

    float dist = length(uv)+0.0001;
    float fog = clamp(dist*2.0,0.0,1.0);
    vec2 tunnel_uv = vec2(4. * safe_atan2_pi(uv.y, uv.x) + t * 0.7, t + 0.7/dist);
    tunnel_uv = fract(tunnel_uv);
    ivec2 int_uv = ivec2(65536. * tunnel_uv);
    float xor_texture = float(int_uv.x ^ int_uv.y) * (1./65536.);

    vec4 color = vec4(0.0,0.0,0.0,1.0);
    color.rgb = vec3(fog * fract(t * 0.4 + xor_texture));
    color.a = 1.0;
    return color;
}

#define ANTIALIAS

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#ifdef ANTIALIAS
    fragColor = (
        sampleAt(fragCoord + vec2(0.00, 0.25)) +
        sampleAt(fragCoord + vec2(0.25, 0.75)) +
        sampleAt(fragCoord + vec2(0.50, 0.00)) +
        sampleAt(fragCoord + vec2(0.75, 0.50))
    ) * 0.25;
#else
    fragColor = sampleAt(fragCoord);
#endif
}
