float circle(vec2 uv, float radius, float blur,  vec2 pos)
{

    float d = distance(pos,uv);
    float value = smoothstep(radius, radius + blur, d);

    return value; 
}

float rect (vec2 uv, vec2 pos, vec2 size)
{
	float value = step(pos.x - size.x/2.,uv.x) - step(pos.x + size.x/2.,uv.x);
    return value *= step(pos.y - size.y/2.,uv.y) - step(pos.y + size.y/2.,uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

	vec2 mouse = vec2(iMouse)/vec2(iResolution.xy);
    vec2 mousePos = vec2(mouse.x * ratio, mouse.y);

    vec2 floorPos = vec2(.5 * ratio, .1);
    vec2 bod1Pos = vec2(.25 * ratio, .5);
    vec2 bod2Pos = vec2(.5 * ratio, .5);
    vec2 bod3Pos = vec2(.75 * ratio, .5);

    vec2 h1Pos = vec2(.25 * ratio, .68);
    vec2 h2Pos = vec2(.5 * ratio, .68);
    vec2 h3Pos = vec2(.75 * ratio, .68);

    vec2 bar1Pos = vec2(.25 * ratio, .3);
    vec2 bar2Pos = vec2(.5 * ratio, .3);
    vec2 bar3Pos = vec2(.75 * ratio, .3);

    vec2 base1Pos = vec2(.25 * ratio, .2);
    vec2 base2Pos = vec2(.5 * ratio, .2);
    vec2 base3Pos = vec2(.75 * ratio, .2);

    vec2 s1Pos = vec2(.23 * ratio, .65);
    vec2 s2Pos = vec2(.5 * ratio, .72);
    vec2 s3Pos = vec2(.73 * ratio, .55);

    float scope = circle(uv, .3, .1, mousePos);
    float yline = rect(uv, mousePos, vec2(.005, .6));
    float xline = rect(uv, mousePos, vec2(.6, .005));

    float floor = rect(uv, floorPos, vec2(2., .2));

    float bod1 = rect (uv, bod1Pos, vec2(.2, .25));
    float bod2 = rect (uv, bod2Pos, vec2(.2, .25));
    float bod3 = rect (uv, bod3Pos, vec2(.2, .25));

    float h1 = circle(uv, .08, .0, h1Pos);
    float h2 = circle(uv, .08, .0, h2Pos);
    float h3 = circle(uv, .08, .0, h3Pos);

    float bar1 = rect(uv, bar1Pos, vec2(.05, .3));
    float bar2 = rect(uv, bar2Pos, vec2(.05, .3));
    float bar3 = rect(uv, bar3Pos, vec2(.05, .3));

    float base1 = rect(uv, base1Pos, vec2(.15, .05));
    float base2 = rect(uv, base2Pos, vec2(.15, .05));
    float base3 = rect(uv, base3Pos, vec2(.15, .05));

    float s1 = circle(uv, .01, .0, s1Pos);
    float s2 = circle(uv, .01, .0, s2Pos);
    float s3 = circle(uv, .01, .0, s3Pos);

    vec3 col = vec3(.2470, .3098, .2431);
    col = mix(col, vec3(.2294, .1156, .0568), floor);
    col = mix(col, vec3(.4294, .3156, .2568), bar1);
    col = mix(col, vec3(.4294, .3156, .2568), bar2);
    col = mix(col, vec3(.4294, .3156, .2568), bar3);
    col = mix(col, vec3(.4294, .3156, .2568), base1);
    col = mix(col, vec3(.4294, .3156, .2568), base2);
    col = mix(col, vec3(.4294, .3156, .2568), base3);
    col = mix(vec3(.3882, .3647, .3529), col, h1);
    col = mix(col, vec3(.3882, .3647, .3529), bod1);
    col = mix(vec3(.3882, .3647, .3529), col, h2);
    col = mix(col, vec3(.3882, .3647, .3529), bod2);
    col = mix(vec3(.3882, .3647, .3529), col, h3);
    col = mix(col, vec3(.3882, .3647, .3529), bod3);
    col = mix(vec3(0.), col, s1);
    col = mix(vec3(0.), col, s2);
    col = mix(vec3(0.), col, s3);

    col = mix(col, vec3(0.), yline);
    col = mix(col, vec3(0.), xline);
    col = mix(col, vec3(0.), scope);

    // Output to screen
    fragColor = vec4(col,1.0);
}
