void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   	
    vec2 xy = fragCoord;
    vec4 webcam = camchan(xy);
    vec4 melt = vec4(
        abs(melting(xy).x)*0.5,
        abs(melting(xy).y)*0.025,
        abs(melting(xy).z)*0.025,
        0.
        );
    vec4 presh = tex(xy);
    float r, g, b, pressure;
  	float rh, gh, bh;
    float texbright = (melt.r + melt.g + melt.g) * 0.333;
    float lumens = (webcam.r + webcam.g + webcam.g) * 0.333;
    pressure = presh.w;
  // fragColor = herbivore + (webcam * vec4(r, g, b, 1.)) + vec4(r*brightness, g*brightness, b*brightness, 1.); //  * vec4(1.0);
	//fragColor = webcam  + sin(tex(fragCoord).w * blackwhite);
    if ( pressure > 1.5 ) { 
        fragColor = melt + (webcam*vec4(0.2))  + sin(tex(fragCoord).w * yellow);
    } else if (pressure > 0.5 && lumens < 0.25) {
        fragColor = melt +(webcam*vec4(3.2)) + (sin(tex(fragCoord).w * vec4(0.)));  
    } else {
       fragColor = melt + (webcam*vec4(1.))  + sin(tex(fragCoord).w * yellow);
    }


}

