void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// x,y => pos
	// z => pressure
	// w => ink

	vec2 xy = fragCoord.xy;
	vec4 webcam = camchan(xy);
	vec2 s = iResolution.xy;
	vec2 g = fragCoord;
	vec4 a = tex(g+vec2(1,0));// + webcam.r;
	vec4 b = tex(g+vec2(0,1));// + webcam.g;
	vec4 c = tex(g+vec2(-1,0));// + webcam.b;
	vec4 d = tex(g+vec2(0,-1));// + (webcam.r * -0.0125);
	float lumens = (webcam.r + webcam.g + webcam.b)*0.333;
	float presh = abs(tex(xy).z);
	float ink = abs(tex(xy).w);

	// fragColor = tex(g-tex(g).xy); // last state
    vec4 tomachi = vec4(1.0001,1.0002,1.0003,0.0);

	vec2 gp = vec2(a.z-c.z,b.z-d.z); // pressure gradient
	vec4 left = camchan(xy+vec2(1,0)) + tex(xy+vec2(1,0));
	vec4 right = camchan(xy+vec2(0,1)) + tex(xy+vec2(0,1));
	vec4 up = camchan(xy+vec2(-1,0)) + tex(xy+vec2(-1,0));
	vec4 down = camchan(xy+vec2(0,-1))+ tex(xy+vec2(1,0));
	//vec2 presh = vec2(left.z-right.z,up.z-down.z); // pressure gradient
	float magic = 0.25;
    fragColor = tex(g-tex(g).xy)* tomachi; // last state

	if (lumens > 2.5 || webcam.r > 0.7 || webcam.g > 0.57) {
		//emit(xy, vec2( 0.8995+(webcam.r*-0.76), 0.4+(webcam.g*-0.95) + (webcam.b*-0.5)));
		//emit(vec2(1.+lumens*-0.5,1.+lumens*-0.5), vec2(1.+lumens*-0.5,1.+lumens*-0.5));
		emit(xy, vec2(0.1+lumens*-0.05,4.+lumens*-2.));

        magic = 0.252;
	}  
	float pressure = magic * (a.z + b.z + c.z + d.z) - 0.05 * (c.x - a.x + d.y - b.y);

	if (lumens < 0.67 ) {
        if (pressure < 0.0005) {
       	   // pressure *= 0.5;    
       		fragColor.z *= 0.9;
			fragColor.w *= 0.9;
			//fragColor.xy *= 1.001;
			//fragColor.xy *= vec2(1.0001);
        } else {
         	pressure *= 1.000069;   
        }
    }  
    if (lumens > 0.0595) {
		//emit(xy, vec2(-0.500, 2.0 + (lumens*-4.) ));
		pressure += (lumens*lightpressure);
    } else {
        pressure *= (lumens*0.92);
		ink = lumens *-1.;
    }



	//emit(xy, 0.005);
	if (abs(d.b) > 0.995 && abs(a.r) > 0.995) // governs over pressure condition
	{
		vec2 p = vec2( webcam.r, webcam.g);
		vec2 dir = vec2( webcam.g, webcam.b);
		emit(xy, p*dir);
    	//emit(xy, dir);
		//emit(vec2(0.1+lumens*-0.05,2.+lumens*-0.5), vec2(1.+lumens*-0.5,1.+lumens*-0.5));

		//emit(p, dir);
		//mit(vec2(xy.y,3.), p);
		//emit(vec2(xy.y,3.), dir);
		//emit(vec2(xy.y,3.), dir);
		pressure *= 0.96;		
	} 
	if (g.x < 0.9 || g.y < 0.9 || g.x > s.x - 0.9 || g.y > s.y - 0.9) fragColor.xy *= 0.;

	if ( pressure > 4.99310589015 || lumens > 0.8) {
		fragColor.w = abs(fragColor.z) -0.05;
		//fragColor.xy = vec2(0.,0.);
	} 
    if ( pressure > 12.5) {
        pressure *= 0.9;

    }
	// x,y => pos
	// z => pressure
	// w => ink
	fragColor.xyz = vec3(
		fragColor.x + gp.x + (webcam.r * 0.0099),
		fragColor.y + gp.y + (webcam.b * 0.0099),// + webcam.r,
		pressure
	);

//	fragColor.xyz = vec3(
