float Xor(float a, float b){
 return a*(1.-b)+   b*(1.-a);
}
float HexDist(vec2 p){
	p = abs(p);
    float c= dot(p,normalize(vec2(1,1.73)));
    c=max(c,p.x);   
    return c;
}

vec4 HexCoords(vec2 uv){
    vec2 r=vec2(1.,1.73);
    vec2 h=r*.5;
    vec2 a=mod(uv,r)-h;
    vec2 b=mod(uv-h,r)-h;

    vec2 gv;
    if (length(a)<length(b)) {
     gv=a;   
    } else {
        gv=b;
    }

    float x=atan(gv.x,gv.y);
    float y=.5-HexDist(gv);
    vec2 id = uv-gv;
    return vec4(x,y,id.x,id.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.47*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    uv*=10.;

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float m=0.1;
    float t = iTime;

    for(float y=-1.; y<=1.; y++) {
    	for(float x=-1.; x<=1.; x++) {
            vec4 hc=HexCoords(uv+100.);
    		float c=smoothstep(.02,.1,tan(hc.x+(t*.5+.5)));

    		vec2 offs = vec2(x,y);
            float d=length(gv+offs);
            float dist=length(id-offs)*.3;
            float r = mix(.3,1.5,sin(dist-t)*.5+.5);
            m=Xor(m,smoothstep(.2,.2,c));
    	}
    }



    col+=m;

    fragColor = vec4(col,1.0);
}
