float random (vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    // scale time to control speed
    float t = iTime * 2.;
    // make a value swing between 0 and 1, with some pause at both sides
    vec2 m = iMouse.xy / iResolution.xy;
    float fade = iMouse.z > 0. ? m.x : clamp(sin(t) + .5, 0., 1.);
    vec2 texCoords = uv;
    float offset = random(uv) * .7;
    float f = clamp(sin(-.3 + fade * 3.7), .0, 1.);
    texCoords.x += offset * f;
    float fader = step(texCoords.x, fade);
    vec3 video_a = texture(iChannel0, texCoords).rgb,
        video_b = texture(iChannel1, texCoords).rgb,
        video_out = mix(video_a, video_b, fader);
    vec3 final_output = uv.y > 0.02 ? video_out : vec3(step(uv.x,fade));
    fragColor = vec4(final_output, 1.0);
}
