//FabriceNeyret2, iapafoto, and I reduce it down to 273 characters! Fits in 2 Tweets!
void mainImage(out vec4 O, vec2 U)
{
    vec2 R = iResolution.xy,
         p = 9.5* (U+U-R)/R.x,  S = sign(p),
         n = .25-S.xx/4.,
         t = .5*iTime + n ,
         s = vec2( 0, round(p-t)+t )*.1,
         c = p - p*s*s,
         r = abs( ( fract( c - vec2(t.x-.5,n) ) -.5 )
                  * vec3( 1, p/c ).xz
                  * mat2( cos( vec4(0,11,33,0)
                           -S.x*(3.*s*s*(ceil(c-n)+n-.5)).y )));
    O += .5 + ( (r+r.y-.5) *R/S/40. ).x;
}
