float prerand(in vec2 c) {
    vec2 rand_gen = vec2(1.14514, -1.919810);
    float time = (iTime + 200.0) * 0.01;
    rand_gen = mat2(cos(time), sin(time), -sin(time), cos(time)) * rand_gen;   
    return fract(rand_gen.x * sin(rand_gen.y * c.x + c.y));
}

float rand(in vec2 c) {
    return prerand(vec2(prerand(c.xy), prerand(c.yx)));
}

float prenoise(in vec2 c) {
    c = c * 15.0;
    vec2 i = floor(c);
    vec2 f = smoothstep(0.0, 1.0, fract(c));
    float a00 = rand(i);
    float a10 = rand(i + vec2(1.0, 0.0));
    float a01 = rand(i + vec2(0.0, 1.0));
    float a11 = rand(i + vec2(1.0, 1.0));
    return a00 * (1.0 - f.x) * (1.0 - f.y)
        + a10 * f.x * (1.0 - f.y)
        + a01 * (1.0 - f.x) * f.y
        + a11 * f.x * f.y;
}

float noise(in vec2 c) {
    return prenoise(vec2(prenoise(c), prenoise(vec2(prenoise(c), prenoise(c.yx)))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / max(iResolution.x, iResolution.y);
    float r = noise(uv);
    float g = noise(uv.yx);
    float b = noise(vec2(r, g));
    fragColor = vec4(r, g, b, 1.0);
}
