// Raymarching
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
#define MIN_DIST 0.002
#define MAX_DIST 1000.
#define ITERATION 200
#define MAT_VOID vec3(-1)

#define AMB_COL vec3(.2, 0.4717, 0.8500)
#define AMB_STRENGTH 0.6100
#define FOG_COL vec3(1., 0.7717, 0.6500)
#define FOG_START 0.5740

// Cheap Rotation by las:
// http://www.pouet.net/topic.php?which=7931&page=1
#define R(p, a) p=cos(a)*p+sin(a)*vec2(p.y,-p.x)
vec3 rot(vec3 p,vec3 r){
    R(p.xz, r.y);
    R(p.yx, r.z);
    R(p.zy, r.x);
    return p;
}

// "init": initialize camera and lights.
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3  Camera_pos;
vec4  Camera_quat;
float Camera_fov;
vec3  sdLight001_dir;
vec3  sdLight001_col;
float sdLight001_clip_start;
float sdLight001_clip_end;
float sdLight001_softness;
#define L0_dir sdLight001_dir
#define L0_col sdLight001_col
#define L0_str sdLight001_clip_start
#define L0_end sdLight001_clip_end
#define L0_sft sdLight001_softness
vec3  sdLight002_dir;
vec3  sdLight002_col;
float sdLight002_clip_start;
float sdLight002_clip_end;
float sdLight002_softness;
#define L1_dir sdLight002_dir
#define L1_col sdLight002_col
#define L1_str sdLight002_clip_start
#define L1_end sdLight002_clip_end
#define L1_sft sdLight002_softness

void init()
{
	Camera_pos = vec3(0., 38., 4.2);
	Camera_quat = vec4(0.0205, 0.7140, 0.6996, 0.0200);
	Camera_fov = 0.085;
	sdLight001_dir = normalize(vec3(-0.5, 0.5, 0.5));
	sdLight001_col = vec3(1., 0.9081, 0.7761)*2.;
	sdLight001_clip_start = 0.0010;
	sdLight001_clip_end = 50.;
	sdLight001_softness = 10.;
	sdLight002_dir = normalize(vec3(0.0538, -0.6674, -0.7427));
	sdLight002_col = vec3(0.1523, 0.1523, 0.1523)*.5;
	sdLight002_clip_start = 0.0500;
	sdLight002_clip_end = 30.;
	sdLight002_softness = 50.;

}

vec3 ro = vec3(0), rd = vec3(0);
vec3 col = vec3(0);

// "camera": create camera vectors.
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3 quat_rotate(vec4 quat, vec3 dir)
{
    return dir + 2.0 * cross(quat.xyz, cross(quat.xyz, dir) + quat.w * dir);
}

void camera(vec2 uv)
{
    vec3 dir = normalize(vec3(0,-.025,-1));//quat_rotate(Camera_quat, vec3(0,0,-1)).xzy;
    vec3 up = quat_rotate(Camera_quat, vec3(0,1,0)).xzy;
    vec3 pos = Camera_pos.xzy;
    float fov = Camera_fov;
    vec3 target = pos-dir;

    vec3 cw = normalize(target - pos);
    vec3 cu = normalize(cross(cw, up));
    vec3 cv = normalize(cross(cu, cw));

    mat3 camMat = mat3(cu, cv, cw);
    rd = normalize(camMat * normalize(vec3(sin(fov) * uv.x, sin(fov) * uv.y, -cos(fov))));
    ro = pos;
}

// SDF functions
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float sdCappedCylinder( vec3 p, vec2 h )
{
    vec2 d = abs(vec2(length(p.xz),p.y)) - h;
    return ((min(max(d.x,d.y),0.0) + length(max(d,0.0))))-0.0;
}

float vmax(vec3 v){
    return max(max(v.x, v.y), v.z);
}

float sdBox(vec3 p,vec3 b)
{
    vec3 d=abs(p)-b;
    return length(max(d,vec3(0)))+vmax(min(d,vec3(0.0)));
}

float fOpUnion(in float a,in float b)
{
    return a<b?a:b;
}

vec4 v4OpUnion(in vec4 a,in vec4 b)
{
    return a.x<b.x?a:b;
}

// scene: Shapes.
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
#define MAT_WHITE vec3(0.6415, 0.5984, 0.4882)*.4
#define MAT_BRICK vec3(0.5025, 0.2454, 0.1800)*.7

vec4 sdScene(vec3 p)
{
    float d = MAX_DIST;
    vec4 res = vec4(MAX_DIST, MAT_VOID);
    p.y+=iTime*.05;
    R(p.xz, sin(iTime*.25)*.5);

    float n = sms(.5, .0, fbm(p, 2)*fbm(p*8.+1., 3))*.5 + noise(p*60.)*.025;

    p += noise(p*100.+1.)*.002 + n*.01;

    vec3 q = p;

    const float MARGIN = 2.8;
    const float MOD_SIZE = 2.1;
    const float ANIM_SPEED = 1.;
    float rounded;

    q.y = mod(q.y, MOD_SIZE)-MOD_SIZE*.5;

    // Radius
    float minR = .025;
    float maxR = MOD_SIZE*.3; // 0.5 == over all
    float stepR = floor((sin(iTime*ANIM_SPEED)+1.)*5.)*.1; // .0, .1, .2, ... 1.  
    float r = stepR * maxR + minR;

    q.x -= MARGIN*.5;

    // Cube bricks
    rounded = sdBox(q, vec3(1)-r)-r;
	d = sdBox(q, vec3(1)-minR)-minR;
    d += abs(d-rounded)*n;
    res = v4OpUnion(vec4(d, MAT_BRICK), res);

    q.x += MARGIN;

    // Cylinder bricks
    rounded = sdCappedCylinder(q, vec2(1)-r)-r;
    d = sdCappedCylinder(q, vec2(1)-minR)-minR;
    d += abs(d-rounded)*n;
	res = v4OpUnion(vec4(d, MAT_BRICK), res);

    // White glue
    q=p;
    q.y += MOD_SIZE*.5;
    q.y = mod(q.y, MOD_SIZE)-MOD_SIZE*.5;
    q+=n*.1;

    q.x -= MARGIN*.5;
    d = sdBox(q, vec2(.95, .05).xyx-minR)-minR;
    res = v4OpUnion(vec4(d, MAT_WHITE), res);    

    q.x += MARGIN;
    d = sdCappedCylinder(q, vec2(.95, .05)-minR)-minR;
    res = v4OpUnion(vec4(d, MAT_WHITE), res);

    res.x*=.75;
    return res;
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec4 intersect()
{
    float d = 1.;
    vec3  m = MAT_VOID;

    int i = 0;
    for (int i = ZERO; i < ITERATION; i++)
    {
        vec3 p = ro + d * rd;
        vec4 res = sdScene(p);
        m = res.yzw;
        res.x *= .5;
        if (abs(res.x) < MIN_DIST || res.x >= MAX_DIST) break;
        d += res.x;
        if (d >= MAX_DIST) break;
    }

    return vec4(d,m);
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3 normal(vec3 p)
{
    // Copy from iq shader.
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*sdScene(p+0.0005*e).x;
    }
    return normalize(n);
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float shadow(vec3 o, vec3 n)
{
    float mint=L0_str;
    float maxt=L0_end;
    float k = L0_sft;
    float res = 1.;
    float t=mint;
    for( int i=0; i < ITERATION; i++)
    {
        float h = sdScene(o + L0_dir*t).x;
        res = min( res, k*h/t);
        t += h;
        if( res<0.0001 || t>maxt ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// "Hemispherical SDF AO" by XT95:
// https://www.shadertoy.com/view/4sdGWN
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3 randomSphereDir(vec2 rnd)
{
    float s = rnd.x*PI*2.;
    float t = rnd.y*2.-1.;
    return vec3(sin(s), cos(s), t) / sqrt(1.0 + t * t);
}
vec3 randomHemisphereDir(vec3 dir, float i)
{
    vec3 v = randomSphereDir( vec2(hash11(i+1.), hash11(i+2.)) );
    return v * sign(dot(v, dir));
}
float ambientOcclusion( in vec3 p, in vec3 n, in float maxDist, in float falloff )
{
    const int nbIte = 12;
    const float nbIteInv = 1./float(nbIte);
    const float rad = 1.-1.*nbIteInv;

    float ao = 0.0;

    for( int i=0; i<nbIte; i++ )
    {
        float l = hash11(float(i))*maxDist;
        vec3 aord = normalize(n+randomHemisphereDir(n, l )*rad)*l;

        ao += (l - max(sdScene( p + aord ).x,0.)) / maxDist * falloff;
    }

    return clamp( 1.-ao*nbIteInv, 0., 1.);
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float specular(vec3 p, vec3 n, vec3 ld)
{
    float power = 5.;
    vec3 to_eye = normalize(p - ro);
    vec3 reflect_light = normalize(reflect(ld, n));
    return pow(max(dot(to_eye, reflect_light), 0.), power);
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
void render()
{
    vec4 hit = intersect();
    vec3 p = ro + hit.x * rd;
    vec3 base_col = hit.yzw;

    if (hit.x>=MAX_DIST)
    {
        col=AMB_COL*.1;
    }
    else
    {
        vec3 n = normal(p);

        float light1 = saturate(dot(n, L0_dir));
        float light2 = saturate(dot(n, L1_dir));
        float shadow = shadow(p+n*.0001, n);

        float ao=0.;
        ao = ambientOcclusion(p, n, .1, 5.);
        ao += ambientOcclusion(p, n, .5, 5.);
        ao += ambientOcclusion(p, n, 2., 2.);
        ao += ambientOcclusion(p, n, 4., 2.);
        ao = smoothstep(0., 4., ao);

        vec3 shadeLight1 = vec3(L0_col*light1*1.5);
        vec3 shadeLight2 = vec3(L1_col*light2);

        col = shadeLight1;
        col *= shadow;
        col += light2*ao*1.;
        col += ao*AMB_COL*1.;
        col *= base_col;
    }
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3 ACESFilm(vec3 x){
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = (uv*2.-1.)*iResolution.y/iResolution.x;
    uv.x *= iResolution.x / iResolution.y;

    init();
    camera(uv);
    render();
    col *= vec3(1.-pow(length(uv), 2.5)*.5);
	col = ACESFilm(col);

    fragColor = vec4(col, 1.);
}

