// Fits half of a sine wave between two points:
float curve(in vec2 p, in vec2 a, in vec2 b, in float thickness) {
    if (min(a.x, b.x) < p.x && p.x <= max(a.x, b.x)) {
        float sine = sin(((p.x - a.x) / (b.x - a.x) * 2.0 - 1.0) * 1.5707963267);
        float cy = a.y + (0.5 + 0.5 * sine) * (b.y - a.y);
        return smoothstep(thickness, 0.0, abs(p.y - cy));
    }

    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float color = 0.0;
    vec2[5] points;

    // Calculate points' positions based on the time and draw them:
    for (int i=0; i < points.length(); i++) {
        points[i] = vec2(i - points.length() / 2, sin(float(i) - iTime) * (mod(float(i), 2.0) * 2.0 - 1.0));
        color += smoothstep(0.05, 0.0, length(uv - points[i]) - 0.1);
    }

    // Draw a curve between pairs of points:
    for (int i=1; i < points.length(); i++) {
        color += curve(uv, points[i - 1], points[i], 0.05);
    }

    fragColor = vec4(vec3(color), 1.0);
}
