// based on Martijn SteinruckenÂ´s (aka The Art of Code/BigWings) 
// ray marching sample and trefoil node sample

#define MAX_STEPS 1000
#define MAX_DIST 100.
#define SURF_DIST .001
#define PI 3.1415926

#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34,233.53));
    p += dot(p, p+23.234);
    return fract(p.x*p.y);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdBox2d(vec2 p, vec2 s) {
    p.x = abs(p.x)-s.x;
	p.y = abs(p.y)-s.y/1.;
    return length(max(p, 0.))+min(max(p.x, max(p.x, p.y)), 0.);
}

float sdTorus(vec3 p, vec3 s) {
    float rMax= 1.4;
    float rMin= .2;

    float radians = atan(p.x, p.z);
    mat2 rotMat = Rot(iTime+0.+radians*0.25);
    mat2 rotMat2 = Rot(3.1415/2.);
    vec2 cp= vec2(length(p.xz)-rMax, p.y); 
    cp *= rotMat;
    vec2 cp2 = cp*rotMat2;
    cp.y = abs(cp.y)-.4;
    cp2.y = abs(cp2.y)-.4;
    //float d= length(cp)-rMin; // formula to have a circle as cross section
    //float d= sdBox2d(cp, vec2(.003,+.2*(1.+pow(sin(radians*20.),4.0000))));
    float d= sdBox2d(cp, 3.*vec2(.03,+.04+.12*abs(pow(sin(PI/2.+radians*6.25),6.))));
    cp.y = abs(cp.y)-0.4;
    float d2= sdBox2d(cp2, 3.*vec2(.03,+.04+.12*abs(pow(sin((radians)*6.25),6.))));// sdBox2d(cp, vec2(.01,+.07)); //length(vec2(length(p.xz)-(rMax+.6), p.y))-rMin;
	return min(d-.03,d2-.03);
}

float GetDist(vec3 p) {
    float d = sdTorus(p, vec3(1));

    return d*.4 ;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=.0;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS; // smaller steps to avoid glitches
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

vec3 Bg(vec3 rd) {
	float k= rd.y*.5+.5;
    vec3 col = mix(vec3(.2,.1,.1), vec3(.2,.5,1.), k);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);

    col +=Bg(rd);
    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;
    	vec3 n = GetNormal(p);

        vec3 r = reflect(rd,n);
    	float spec = pow(max(0., r.y),30.);
        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
    	col = mix(Bg(r), vec3(dif), .5) + spec;  
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
