#define PI 3.1415926

mat2 rotate(float a) {
    return mat2(
        cos(a), -sin(a),
        sin(a), cos(a)
    );
}

vec2 kscope(vec2 p, float factor) {
    float sector = PI * 2.0 / factor;
    float r = length(p);
    float phi = atan(p.x, p.y);

    float phi1 = mod(phi, sector);
    phi1 = mod(phi / sector, 2.0) < 1.0
        ? phi1
        : sector - phi1;

    return vec2(r * cos(phi1), r * sin(phi1));
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float scene(vec2 p) {
    float d = 10e20;
    d = min(d, sdLine(p, vec2(0.0, 0.0), vec2(0.4, 0.0)));

    d = min(d, sdLine(p, vec2(0.2, 0.0), vec2(0.3, 0.1)));
    d = min(d, sdLine(p, vec2(0.3, 0.0), vec2(0.36, 0.06)));

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = uv.x * r + (0.5 - 0.5 * r);
    vec2 sc = mod(uv * 8.0 * rotate(0.3) + vec2(sin(iTime) * 0.4, iTime), 1.4);
	vec2 p = kscope((sc - 0.5) * rotate(iTime + uv.y), 12.0);

    vec4 color = scene(p) < 0.02
        ? vec4(0.8 - p.y, 1.0 - p.y * 0.5, 1.0, 1.0)
        : vec4(0.7 - uv.y * 0.5, 0.85 - uv.y * 0.5, 0.9, 1.0);
    fragColor = color;
}
