#define PI 3.14159265
#define MAXIT 80
#define EPSILON 0.08

#define minx4(a, b) ((a.w) < (b.w) ? (a) : (b))
#define minx2(a, b) ((a.x) < (b.x) ? (a) : (b))

#define MAT0 0.0
#define MAT1 1.0

mat3 rotX(float a)
{
    return mat3(
        1., 0., 0.,
        0., cos(a), -sin(a),
        0., sin(a), cos(a)
    );
}

mat3 rotY(float a)
{
    return mat3(
        cos(a), 0.0, -sin(a),
        0., 1., 0.,
        sin(a), 0.0, cos(a)
    );
}

mat3 rotZ(float a)
{
    return mat3(
        cos(a), -sin(a), 0.,
        sin(a), cos(a), 0.,
        0., 0., 1.
    );
}

float t;

float sphere(vec3 r, float a)
{
    return length(r) - a;
}

float torus(vec3 r, vec2 a)
{
    vec2 p = vec2(length(r.xz) - a.x, r.y);
    return length(p) - a.y;
}

float plane(vec3 r, vec3 o, vec3 n) {
    return dot(r - o, n);
}

float cylinder(vec3 r, vec2 a)
{
	vec2 p = abs(vec2(length(r.xz), r.y)) - a;

	return min(max(p.x, p.y), 0.0) + length(max(p, 0.));
}

float hash(vec2 r) {
    return fract(sin(dot(r, vec2(15.5921, 96.654654))) * 23626.3663);
}

float box(vec3 r, vec3 a, vec2 s)
{
	r.y *= 0.45;
	r.y *= 1. + cos(max(-r.y, 0.)) * 0.05;
	r.y -= max(r.y, 0.5) * 0.33;
	r.y *= 1. + cos(r.x * 0.3) * cos(r.z * 0.2) * 0.01 * max(r.y, 0.);
	r.y *= 1. + round(mod(r.x * 0.105, 1.)) * round(mod(r.z * 0.105, 1.)) * 0.009 * max(r.y, 0.) * hash(r.xz) * hash(r.xz) * 1.5;

    vec3 p = (abs(r) - a) - max(abs(r.y) + r.y * r.y * 0.1, 2.) + fract(abs(sin(r.x + 0.05)) + hash(r.xx)) * 4. + fract(abs(sin(r.z))) * 4. - 10.;

    return length(max(p, 0.));
}

float shade(vec3 n, vec3 rd)
{
    return clamp(max(dot(n, -rd), 0.) + 1., 0., 1.);
}

vec3 fog(float z, vec3 col, vec3 fogCol)
{
    return mix(fogCol, col, exp(-z));
}

vec2 map(vec3 r)
{    
	r *= rotY(t);
	r.z += t * 50.;
	r.x += 10. * sin(r.z * 0.015) + 50.;

    float b = 0.7;
    vec3 rr = vec3(0.);
    rr.x = round(r.x / b) * b;
    rr.y = round(r.y / b) * b;
    rr.z = round(r.z / b) * b;

    vec2 b1 = vec2(
        box(mod(rr + 50., 100.) - 50., vec3(2.1, 2.1, 2.1), r.xy),
        (int(sin(rr.x * 0.1) * 2. + sin(rr.y * 0.1) * 2. + sin(rr.z * 0.1) * 2.) % 2)
    );

    return b1;
}

vec3 matCol(vec2 o)
{
    if (o.y == MAT0)
        return normalize(vec3(0.8, 0.5, .2));

    if (o.y == MAT1)
        return normalize(vec3(0.7, 0.55, .2));

    return vec3(0.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    t = -iTime * 0.4;

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    mat3 cam = rotY(-PI) * rotX(0.3);

    vec3 ro = vec3(0., 10.0, -20.0);
    vec3 rd = cam * normalize(vec3(uv * 2., -1.));
    vec3 r = ro;

    vec3 bcol = vec3(0.5, 0.5, 0.) * 0.2;
    vec4 col = vec4(0.);
    col.rgb = bcol;

    float sh = 1.;

    for (int i = 0; i < MAXIT; ++i) {
        vec2 d = map(r);
        float z = length(r - ro);

        if (d.x < EPSILON) {
            col.rgb = mix(col.rgb, 
				matCol(d), 
				shade(normalize(r), rd));
            col.rgb = fog(z * 0.02, col.rgb, bcol);
            break;
        }

        d.x *= 0.7 - 0.1 * hash(uv);
		r += rd * d.x * 0.5;

        sh = (float(i) / float(MAXIT));
    }

    if (sh < 0.5)
		col.rgb *= exp(-sh * 2. + 1.);

    fragColor = vec4(col.rgb, 1.);
}
