float circle(vec2 uv,float radius, float blur,  vec2 pos)
{
    float d = distance(pos,uv);
    float value = smoothstep(radius, radius + blur, d);

    return value; 
}

float rect (vec2 uv, vec2 pos, vec2 size)
{
	float value = step(pos.x - size.x/2.,uv.x) - step(pos.x + size.x/2.,uv.x);
    return value *= step(pos.y - size.y/2.,uv.y) - step(pos.y + size.y/2.,uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos = vec2(.5 * ratio, .7);
    vec2 barPos = vec2(.5 * ratio, .3);
    vec2 b1Pos = vec2(.5 * ratio, .15);
    vec2 b2Pos = vec2(.5 * ratio, .21);
    vec2 b3Pos = vec2(.5 * ratio, .27);

    float dist = distance(shapePos, uv) * 2.0;

    vec2 uv1 = uv;
    uv1 -= shapePos;

    float angle = atan(uv1.y, uv1.x) + iTime;

    float ring = circle(uv,.33, 0., shapePos);
    ring -= circle(uv, .35, 0., shapePos);

    float back = circle(uv, .1, 0., shapePos);
    float plug = circle(uv, .05, 0., shapePos);
    float bar = rect(uv, barPos, vec2(.13, .7));
    float b1 = rect(uv, b1Pos, vec2(.04, .04));
    float b2 = rect(uv, b2Pos, vec2(.04, .04));
    float b3 = rect(uv, b3Pos, vec2(.04, .04));

    float radius = sin((angle + iTime * 12.)*3.);
    radius *= .6;

    float value = 1.-smoothstep(radius, radius +.1, dist);

    vec3 color = mix(vec3(.1), vec3(1.), back);
    color = mix(color, vec3(.1), bar);
    color = mix(color, vec3(1.), b1);
    color = mix(color, vec3(1.), b2);
    color = mix(color, vec3(1.), b3);
    color = mix(color, vec3(.2), value);
    color = mix( vec3(.1), color, plug);
    color = mix(color, vec3(.2), ring);

    // Output to screen
    fragColor = vec4(color*2.5,1.0);
}
