// Rotating Gear
// my step by step tutorial 6
// by Arthur Stammet, 03.2019

// Inspired by Inigo Quilez https://www.youtube.com/watch?v=0ifChJ0nJfM&t=7s

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set the center to the middle of the screen and make the pixels quadratic
    vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y; 

    // change values here --------------------------------------------------------------

    float CenterX = 0.0;
	float CenterY = 0.0;

    float radius = 0.4; // radius of the disk ( 0.5 = height of the screen ) 
    float blur = 0.015; // low values for sharper edges

    // Color of the Gear
    float red = 0.8; 	// 0.0 <> 1.0
	float green = 0.8;	// 0.0 <> 1.0
	float blue = 0.8; 	// 0.0 <> 1.0

    // Background-Color
    float Bred = 0.0; 	// 0.0 <> 1.0
	float Bgreen = 0.0;	// 0.0 <> 1.0
	float Bblue = 0.0; 	// 0.0 <> 1.0

    float rotSpeed = iTime * 10.;
    float modAmp = 0.04; 		// amplitude of the cosine modulation
    float modFreq = 25.; 		// frequency = number of cosine cycles
    float modPhase = rotSpeed; 	// phase of the cosine modulation
    float modDist = 0.; 		// distortion

    // ---------------------------------------------------------------------------------

	vec2 CenterXY = p - vec2( CenterX, CenterY );	// center of the disk

	radius += modAmp * cos(atan(CenterXY.x,CenterXY.y) * modFreq + modDist * CenterXY.x + modPhase);

    vec3 disk = vec3( red, green, blue ); // colorized disk (animated)	
	vec3 back = vec3 ( Bred, Bgreen, Bblue );  // colorized screen (animated)
    disk *= smoothstep( radius, radius-blur, length(CenterXY)); // moving disk (animated)
    vec3 col = disk + back;

    fragColor = vec4(col, 1.0);
}
