// Yellow Fun - Del 03/10/2020
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = (iResolution.xy - 2.0*fragCoord.xy)/iResolution.y;
	float t = iTime+1.0;
	vec3 color1 = vec3( 1.0, 1.0, 0.0 );
	vec3 color2 = vec3( 0.4, 0.4, 0.1 );

	uv.x *= 2.0+sin(t*0.4);
    if (iMouse.z<0.5)
    	uv.x = dot(uv,uv)*0.1;
	float c = floor((uv.x + 0.25)/0.5);
	uv.x = mod(uv.x + 0.25,0.5) - 0.25;
	uv.x *= -(mod(c, 2.0)*2. - 1.);
	float v1 = sin(fract(t*.05)*6.28);
	float v2 =sin(fract(t*.027)*6.28) *24.4; 
	uv.x += sin(fract(uv.x*4.0+t*.6)*6.28+(uv.x*v2+uv.y*35.0)*v1)*0.09;
	float curve = 0.1;
   	vec3 col = mix( color1, color2, smoothstep( 0.5-curve,0.5+curve,uv.x+0.5) );	
	fragColor = vec4(col, 1.0);    
}
