void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec3 col = vec3(0.);
    for(float i=-15.; i<=15.; i++){
        col += texture(iChannel0,(fragCoord+vec2(i,0))/iResolution.xy).rgb/pow((abs(i)+1.),1.5);
    }
    for(float i=-15.; i<=15.; i++){
        col += texture(iChannel0,(fragCoord+vec2(0,i))/iResolution.xy).rgb/pow((abs(i)+1.),1.5);
    }
    fragColor = vec4(col/4.5,1.);
}

//Based off of my fractal voronoi shader. This is a procedural texture I remade here on Shadertoy, that I like to use for starry skys in Blender

vec2 random(float px,float py,float seed){
    float x = fract(sin(dot(vec3(px,py,seed), vec3(45.84018476, 94.30285684, 33.52930515))) * 23859.2590);
    float y = fract(sin(dot(vec3(px,py,seed), vec3(96.95283515, 23.59403928, 59.59203951))) * 96834.1853);
    return vec2(x, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.x;
    float d = iResolution.x;
    float dsum = 0.;
    d*=d;
    float shift = 0.;
    for(float scale=20.; iResolution.x/scale>1.; scale*=2.){
        for(float i=-1.; i<2.; i++){
            for(float j=-1.; j<2.; j++){
                uv = fragCoord/iResolution.x;
                uv+=(scale/3.+500.)*iTime/6000.;
                vec2 uv2 = uv + vec2(i,j)/scale;
                vec2 pos = random(floor(uv2.x*scale),floor(uv2.y*scale),0.);
                vec2 d2 = pos-vec2(uv.x*scale-floor(uv2.x*scale), uv.y*scale-floor(uv2.y*scale));
                d = min(dot(d2,d2),d);
            }
        }
        float temp = max((.015-d)*13.0,0.);
        shift += temp*(random(floor(uv.x*scale),floor(uv.y*scale),0.).x-.5)*2.;
        dsum += temp;
    }
    fragColor = (vec4(vec3(dsum)+vec3(-shift,0.,shift)/3.,1.0)*2.+texture(iChannel0,fragCoord/iResolution.xy))/3.;
}
