// golfed antialiased (+missing inits) version of 1052 chars Artleet's https://shadertoy.com/view/tdKcRK
//                                    inspired by 2817 chars mattz's https://www.shadertoy.com/view/MdfBzl
// -9 chars by cotote

#define h(p) ( fract( sin(  34.*(p) + H*(p).yx ) * H ) * 2. - 1. )       //
#define A      cos( vec2(0,33) + ( S + iTime * .2 )                      //
#define c(p)   cos(.63 * ( vec3(8, 8, 5) * dot( p+v + h(p+v) , A) ) / S + vec3(0, 2, 5))) //
#define a(p)   p+v + .2 * A*h(p+v))                                      //
                                                                         //
#define P( A,B,C,  D ) (                                                  \
        P = a(A),                                                         \
        P = inverse( mat2( a(B)-P, a(C)-P ) ) * ( U -.5 - P ),            \
        w += d = max(0., 1. - H/9.* dot( z = vec4( abs( 1. - P.x - P.y) , abs(P), -1 ), z/z )), \
        d* mat4(.5+.5*mat3(c(I), c(A), c(D))) * z                         \
     )                                                                   //
void mainImage(out vec4 O, vec2 u) { O-=O; O.a++;
    float S = 5., w = 0., d, H = iResolution.y;
    vec2  U = S* u / H * mat2(6, 0, -3, 5) / 6. ,
          P, I = O.wx, J = I.yx, v;
    vec4  z;
    for(int i=0; i<4; i++)
        v = floor(U) - vec2(i%2,i/2),
        O+= P(  ,J,I,  J  ),
        O+= P( J,I,1., 1. );
    O /= w;}
