// Cole Peterson

#define R iResolution.xy
#define ss(a, b, t) smoothstep(a, b, t)
#define A(p) texture(iChannel0, p).x
mat2 rot(float a) {return mat2(cos(a), -sin(a), sin(a), cos(a));}

void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;
    vec3 rd = normalize(vec3(uv, 1.));
    vec3 ro = vec3(0., .44, 0.);

    rd.xy*=rot(.1);
    rd.xz*=rot(iTime*.24+3.14159*.5);
    ro.xz += 1.5*vec2(cos(iTime*.24), sin(iTime*.24));

    float d = 0.0, t = 0.0, ns = 0.;
    vec3 p = vec3(0);

    for(int i = 0; i < 40; i++){
        p = ro + rd*t;
    	d = p.y - A(p.xz*.4)*.35; 

        if(d < 0.001 || t > 100.) break;
        t += d * .95;
        ns++;
    }

    vec3 rg = ss(8., 30., ns) * vec3(.9, .9, .8)*.8+A(vec2(uv.x, uv.y+iTime*.34))*.25;
    vec3 sand = vec3(.8, .6, .4)+abs(sin(p.y*120.+cos(p.z+p.x)*23.))*.022;
    sand += 1.-exp(-rg*.3);
    vec3 sky = mix(vec3(.8, .9, .99), vec3(.1, .4, .99), abs(uv.y));
    vec3 col = mix(sky, sand, exp(-t*t*t*.03));

    col = pow(col, vec3(2.55));
    col = mix(1.-col, col, min(1., iTime*.55));
    col *= ss(.384, .38, abs(uv.y));

    f = vec4(col, 1.0);
}
