/*
  Knob controls speed and direction; mouseable; view direction changes at high elevation;
  on-path distance shown.
*/

float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrCylDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float SmoothMax (float a, float b, float r);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float ShowIntPZ (vec2 q, vec2 cBox, float mxChar, float val);
float Hashfv2 (vec2 p);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

const int npTrail = 512;  // same for image and buffer (the bigger the better)
const int npFst = 6;

vec3 sunDir, qHit, carPos;
vec2 wlBase, wlRot;
float tCur, dstFar, carRot, carTlt, wlRad, hgSize, trWidf;
int idObj;
const int idWhl = 1, idAxl = 2, idMudg = 3, idPlat = 4, idBatt = 5, idHand = 6;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define VAR_ZERO min (iFrame, 0)

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, a;
  dMin = dstFar;
  p -= carPos;
  p.xz = Rot2D (p.xz, carRot);
  p.y -= wlRad;
  p.yz = Rot2D (p.yz, carTlt);
  q = p;
  q.x = abs (q.x) - wlBase.x;
  q.yz = Rot2D (q.yz, - ((p.x > 0.) ? wlRot.x : wlRot.y));
  d = PrRoundCylDf (q.yzx, wlRad - 0.05, 0.05, 0.12);
  a = (length (q.yz) > 0.) ? atan (q.z, - q.y) / (2. * pi) : 0.;
  d = max (d, 0.05 - length (Rot2D (q.yz, 2. * pi * floor (24. * a + 0.5) / 24.) +
     vec2 (wlRad - 0.02, 0.)));
  DMINQ (idWhl);
  q = p;
  q.x = abs (q.x) - wlBase.x + 0.2;
  d = PrRoundCylDf (q.yzx, 0.1, 0.03, 0.4);
  DMINQ (idAxl);
  q = p;
  q.x = abs (q.x) - wlBase.x;
  d = SmoothMax (abs (PrRoundCylDf (q.yzx, wlRad, 0.1, 0.15)) - 0.02,
     max (max (- q.y, q.z) - 0.18, q.x - 0.2), 0.05);
  DMINQ (idMudg);
  q = p;
  d = PrRoundBoxDf (q, vec3 (wlBase.x - 0.3, 0.08, 0.5), 0.05);
  DMINQ (idPlat);
  q = p;
  q.yz -= vec2 (0.4, 0.2);
  d = PrRoundBoxDf (q, vec3 (0.1, 0.4, 0.2), 0.1);
  DMINQ (idBatt);
  q = p;
  q.yz -= vec2 (1.5, 0.2);
  d = min (PrCylDf (q.xzy, 0.08, 1.5), PrCylDf (q.xzy, 0.11, 0.08));
  q.y -= 1.5;
  d = min (d, PrRoundCylDf (q.yzx, 0.04, 0.05, 0.8));
  DMINQ (idHand);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

float TrailShd (vec2 p)
{
  vec3 u;
  vec2 q, g, gp, gMid, dg;
  float st, gLen;
  st = 1.;
  g = carPos.xz;
  for (int j = VAR_ZERO; j < npTrail; j ++) {
    gp = g;
    u = Loadv4 (npFst + j).xyz;
    if (u.z >= 0.) {
      g = u.xy;
      gMid = 0.5 * (g + gp);
      dg = g - gp;
      gLen = length (dg);
      if (gLen > 0.) {
        q = Rot2Cs (p - gMid, dg.yx / gLen);
        q.x = abs (q.x) - wlBase.x;
        st = min (st, 1. - 0.3 * sqrt (1. - float (j) / float (npTrail)) *
           (1. - smoothstep (0., 0.05, PrRoundBox2Df (q, vec2 (0.01, 0.5 * gLen), 0.15))));
      }
    } else break;
  }
  return st;
}

vec3 GrndCol (vec3 ro, float dstGrnd, float sh)
{
  vec3 col, vn;
  vec2 vf, q;
  vec2 gId;
  float a, f, ff, st;
  gId = PixToHex (ro.xz / hgSize);
  q = Rot2D ((ro.xz - HexToPix (gId * hgSize)) / hgSize,
     (2. * step (Hashfv2 (gId), 0.5) - 1.) * pi / 6.);
  q = Rot2D (q, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
  q.x += 1.;
  col = 0.5 * mix (vec3 (0.8, 1., 0.5), vec3 (0.7, 0.9, 0.5), 0.2 +
     0.8 * smoothstep (0.3, 0.7, Fbm2 (4. * ro.xz)));
  vf = vec2 (0.);
  f = abs (length (q) - 0.5) - trWidf;
  if (f < 0.) {
    a = abs (mod (4. * (mod (15. * atan (q.y, - q.x) / (2. * pi), 1.) - 0.5) + 0.5, 1.) - 0.5) - 0.3;
    for (float s = 1.; s >= 0.; s -= 1.) {
      col = mix (vec3 (0.3, 0.5, 1.), col, max (smoothstep (0., 0.02, a - 0.03 * s),
         smoothstep (0., 0.002, abs (f + 0.02) - 0.005)));
    }
  }
  st = TrailShd (ro.xz);
  vf = vec2 (16., 1. - 0.9 * f);
  if (st < 1.) vf.y *= 2.;
  vn = vec3 (0., 1., 0.);
  ff = 1. - smoothstep (0.5, 0.8, dstGrnd / dstFar);
  if (vf.x > 0. && ff > 0.) vn = VaryNf (vf.x * ro, vn, vf.y * ff);
  col *= (0.3 + 0.7 * sh * max (dot (vn, sunDir), 0.)) * st;
  return col;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.4, 0.5);
  } else {
    q = 0.01 * (ro.xz + 2. * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    skCol = vec3 (0.3, 0.4, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) *
       min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
    col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, dstGrnd, r, sh;
  bool isBg;
  isBg = false;
  dstGrnd = dstFar;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    r = length (qHit.yz);
    if (idObj == idWhl) col4 = vec4 (0.8, 0.8, 0.85, 0.1) * (1. - 0.7 * step (0.85, r)) *
       (1. - 0.2 * step (r, 0.3) * smoothstep (0., 0.02,
       abs (mod (12. * atan (qHit.z, qHit.y) / (2. * pi) + 0.5, 1.) - 0.5) - 0.2));
    else if (idObj == idAxl) col4 = vec4 (0.4, 0.4, 0.45, 0.);
    else if (idObj == idMudg) col4 = vec4 (0.2, 0.9, 0.2, 0.1) * (0.5 +
       0.5 * step (0., PrRoundBox2Df (vec2 (qHit.x, r), vec2 (0.15, wlRad), 0.1))) *
       (0.8 + 0.2 * smoothstep (0., 0.02, abs (abs (qHit.x) - 0.05) - 0.01));
    else if (idObj == idPlat) col4 = vec4 (0., 0.7, 0., 0.) * (1. - 
       0.2 * step (abs (qHit.z), 0.5) * step (0., qHit.y) * smoothstep (0., 0.1, mod (8. * qHit.z, 1.) - 0.6));
    else if (idObj == idBatt) col4 = vec4 (0., 0.8, 0., 0.1);
    else if (idObj == idHand) col4 = vec4 (0.8, 0.9, 0.8, 0.2) * (1. - 0.3 * step (0.3, abs (qHit.x)));
  } else if (rd.y < 0.) {
    dstGrnd = - ro.y / rd.y;
    ro += dstGrnd * rd;
    vn = vec3 (0., 1., 0.);
  } else {
    isBg = true;
    col = SkyBgCol (ro, rd);
  }
  if (! isBg) {
    sh = (min (dstObj, dstGrnd) < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
    if (dstObj < dstFar) col = col4.rgb * (0.3 + 0.7 * sh * max (dot (vn, sunDir), 0.)) +
       col4.a * step (0.95, sh) * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.);
    else col = mix (GrndCol (vec3 (ro.xz, 0.).xzy, dstGrnd, sh), vec3 (0.3, 0.4, 0.5), pow (1. + rd.y, 16.));
  }
  return clamp (col, 0., 1.);
}

#define AA  0

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 ro, rd, col, wgBox;
  vec2 canvas, uv, ud, cnPos;
  float el, az, zmFac, asp, onPath, trvDist, s, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (1);
  wlBase = stDat.xy;
  wlRad = wlBase.y;
  stDat = Loadv4 (2);
  hgSize = stDat.x;
  trWidf = stDat.y;
  onPath = stDat.z;
  trvDist = stDat.w;
  stDat = Loadv4 (3);
  az = stDat.x;
  el = stDat.y;
  cnPos = stDat.zw;
  stDat = Loadv4 (4);
  carTlt = stDat.x;
  wlRot = stDat.zw;
  stDat = Loadv4 (5);
  carPos = stDat.xyz;
  carRot = stDat.w;
  el = clamp (el, -0.4 * pi, -0.01 * pi);
  if (el > -0.37 * pi) az += carRot;
  vuMat = StdVuMat (el, az);
  ro = carPos + vuMat * vec3 (0., 3., -12.);
  zmFac = 3. + 2. * el;
  dstFar = 150.;
  sunDir = normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  wgBox = vec3 (0.41, -0.32, 0.135);
  ud = 0.5 * uv - wgBox.xy * vec2 (asp, 1.);
  s = (length (ud) - wgBox.z) * canvas.y;
  col = mix (vec3 (0.5), col, smoothstep (0., 1., abs (s) - 1.));
  if (s < 0.) {
    col = mix (vec3 (0.5), col, step (1., Minv2 (abs (ud)) * canvas.y));
    col = mix (((onPath > 0.) ? vec3 (0., 1., 0.) : vec3 (0.8, 0., 0.)), col,
       smoothstep (2.5, 3.5, abs (length (ud - cnPos) * canvas.y - 10.)));
  }
  col = mix (col, vec3 (0., 1., 0.), ShowIntPZ (0.5 * uv - vec2 (0.44 * asp, -0.15),
     vec2 (0.06 * asp, 0.03), 4., mod (floor (trvDist / (2. * wlRad)), 1e4)));
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float DigSeg (vec2 q)
{
  return (1. - smoothstep (0.13, 0.17, abs (q.x))) *
     (1. - smoothstep (0.5, 0.57, abs (q.y)));
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  vec2 vp, vm, vo;
  float d;
  int k, kk;
  vp = vec2 (0.5, 0.5);
  vm = vec2 (-0.5, 0.5);
  vo = vp - vm;
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.8, 2.3);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowIntPZ (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (Minv2 (q) >= 0. && Maxv2 (q) < 1.) {
    q.x *= mxChar;
    nDig = mxChar;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    val = max (val, 0.);
    v = val / pow (10., mxChar - idChar - 1.);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

// "Segway Driving" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Hashfv2 (vec2 p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

const int npTrail = 512;  // same for image and buffer (the bigger the better)
const int npFst = 6;

vec3 carPos;
float hgSize, trWidf;
const float pi = 3.1415927, sqrt3 = 1.7320508;
const float txRow = 128.;

float OnPath ()
{
  vec2 q, gId;
  q = carPos.xz;
  gId = PixToHex (q / hgSize);
  q = Rot2D ((q - HexToPix (gId * hgSize)) / hgSize, (2. * step (Hashfv2 (gId), 0.5) - 1.) * pi / 6.);
  q = Rot2D (q, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
  q.x += 1.;
  return (abs (length (q) - 0.5) < trWidf) ? 1. : 0.;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat;
  vec3 wgBox;
  vec2 iFrag, canvas, cnPos, wlBase, wlRot, cp, ud, w;
  float tCur, autoMd, carRot, cRotN, strRot, wlRad, rTurn, spd, carTlt, onPath, trvDist, tc, nStep,
     az, el, asp, s;
  int pxId, wgSel, nFrame;
  bool init;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (pxId >= npFst + npTrail) discard;
  nFrame = iFrame;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
 init = (nFrame <= 5);
  asp = canvas.x / canvas.y;
  if (init) {
    mPtrP = mPtr;
    carPos = vec3 (0.);
    carRot = 0.;
    carTlt = 0.;
    wlRad = 1.;
    wlBase = vec2 (1.2, wlRad);
    strRot = 0.;
    wlRot = vec2 (0.);
    autoMd = 1.;
    az = 0.;
    el = -0.37 * pi;
    nStep = 0.;
    cnPos = vec2 (0.);
    wgSel = 0;
    hgSize = 40.;
    trWidf = 0.12;
    onPath = 0.;
    trvDist = 0.;
  } else {
    stDat = Loadv4 (0);
    mPtrP.xyz = stDat.xyz;
    wgSel = int (stDat.w);
    stDat = Loadv4 (1);
    wlBase = stDat.xy;
    nStep = stDat.z;
    autoMd = stDat.w;
    stDat = Loadv4 (2);
    hgSize = stDat.x;
    trWidf = stDat.y;
    onPath = stDat.z;
    trvDist = stDat.w;
    stDat = Loadv4 (3);
    az = stDat.x;
    el = stDat.y;
    cnPos = stDat.zw;
    stDat = Loadv4 (4);
    carTlt = stDat.x;
    strRot = stDat.y;
    wlRot = stDat.zw;
    stDat = Loadv4 (5);
    carPos = stDat.xyz;
    carRot = stDat.w;
  }
  if (pxId < npFst) {
    wgBox = vec3 (0.41, -0.32, 0.135);
    if (mPtr.z > 0.) {
      if (wgSel == 0 && mPtrP.z > 0.) {
        az = -2. * pi * mPtr.x;
        el = - pi * mPtr.y;
      } else {
        ud = mPtr.xy * vec2 (asp, 1.) - wgBox.xy * vec2 (asp, 1.);
        if (wgSel == 1) {
          autoMd = - tCur;
          cnPos = ud;
          s = length (cnPos);
          if (s > 0.) cnPos = min (s, wgBox.z) * cnPos / s;
        } else if (mPtrP.z <= 0. && length (ud) < wgBox.z) wgSel = 1;
      }
    } else {
      wgSel = 0;
      cnPos *= 1. - 5e-3;
    }
    wlRad = wlBase.y;
    ++ nStep;
    if (autoMd > 0.) {
      tc = mod (0.003 * nStep, 4.);
      strRot = 0.12 * pi * SmoothBump (0.3, 0.7, 0.15, mod (tc, 1.)) * sign (mod (tc, 2.) - 1.) *
         sign (tc - 2.);
      spd = 0.05 - 0.03 * abs (strRot / (0.12 * pi));
      w = vec2 (- strRot / (0.15 * pi), spd / 0.5);
      w = pow (abs (w), 1. / vec2 (1.5)) * sign (w);
      cnPos = w * wgBox.z;
    } else {
      w = cnPos / wgBox.z;
      w = pow (abs (w), vec2 (1.5)) * sign (w);
      strRot = -0.15 * pi * w.x;
      spd = max (-0.05, 0.25 * w.y);
      if (tCur + autoMd > 10.) {
        autoMd = 1.;
        el = -0.37 * pi;
      }
    }
    cp = carPos.xz;
    w = vec2 (1.);
    if (abs (strRot) > 1e-4) {
      rTurn = 0.5 / strRot;
      rTurn = max (6. * wlBase.x, abs (rTurn)) * sign (rTurn);
      cRotN = carRot - spd / rTurn;
      carPos.xz += rTurn * (sin (carRot - vec2 (0.5 * pi, 0.)) - sin (cRotN - vec2 (0.5 * pi, 0.)));
      carRot = mod (cRotN, 2. * pi);
      w -= vec2 (-1., 1.) * wlBase.x / rTurn;
    } else {
      carPos.xz += spd * sin (carRot + vec2 (0., 0.5 * pi));
    }
    wlRot += w * spd / wlRad;
    carTlt = -0.4 * spd;
    if (pxId == 2) {
      onPath = OnPath ();
      if (onPath > 0.) trvDist += length (carPos.xz - cp);
      else trvDist = 0.;
    }
  }
  if (! init) {
    if (mod (float (nFrame), 8.) == 0.) {
      if (pxId == npFst) stDat = vec4 (Loadv4 (5).xzw, 0.);
      else if (pxId < npFst + npTrail) stDat = Loadv4 (pxId - 1);
    } else if (pxId >= npFst && pxId < npFst + npTrail) stDat = Loadv4 (pxId);
  } else {
    stDat = vec4 (0., 0., -1., 0.);
  }
  if      (pxId == 0) stDat = vec4 (mPtr.xyz, float (wgSel));
  else if (pxId == 1) stDat = vec4 (wlBase.xy, nStep, autoMd);
  else if (pxId == 2) stDat = vec4 (hgSize, trWidf, onPath, trvDist);
  else if (pxId == 3) stDat = vec4 (az, el, cnPos);
  else if (pxId == 4) stDat = vec4 (carTlt, strRot, wlRot);
  else if (pxId == 5) stDat = vec4 (carPos, carRot);
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}
