#define M_PI 3.14159265358

#define NYAN_W 40
#define NYAN_H 32
#define NYAN_W_F 40.
#define NYAN_H_F 32.

vec3 hsv2rgb( in vec3 c )
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec4 nyan( in vec2 uv, in int index )
{
    index = index % 6;
    ivec2 ruv = ivec2(NYAN_W * index + int(uv.x * float(NYAN_W)), int(uv.y * float(NYAN_H)));
	return texelFetch(iChannel0, ruv, 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float rat = NYAN_W_F / NYAN_H_F;
    float fFrame = float(iFrame);
    float factor = abs(sin(M_PI / 180. * fFrame/5.)) + 2.0;
    vec2 nyan_dim = vec2(NYAN_W_F, NYAN_H_F) * (iResolution.x / NYAN_W_F / 6.);
    vec2 pos = fragCoord + vec2(fFrame * rat, fFrame);
    vec2 fragCoordCapped =
        mod(pos, nyan_dim) / nyan_dim;
	fragColor = nyan(fragCoordCapped, iFrame / 5 + int(pos / nyan_dim.x));

    fragColor = vec4(mix(
        hsv2rgb(vec3(mod(nyan_dim.x*1.2-fragCoord.x + fragCoord.y * rat, nyan_dim.x) / nyan_dim.x, .65, 1.)),
        fragColor.xyz,
        fragColor.a), 1.);;
}
