#define RES iResolution.xy
#define MIN_DIST 0.0001

mat2 rotate(float a) {
    float co = cos(a), si = sin(a);
    return mat2(co, si, -si, co);
}

float smin( float a, float b, float k ) {
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}

float sdCircle(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

int id = 0;
float map(vec3 p) {
    id = 0;
    float mCircle = sdCircle(p, 0.4);
    float sCircle = sdCircle(abs(p) - vec3(0.35,0.35,0), 0.25);
    float d = smin(mCircle, sCircle, 0.1);
    return d;
}

struct hit {
    float t;
    float occlusion;
    int id;
};

hit trace(vec3 ro, vec3 rd) {
    float t = 0., i = 0.;
    float N = 100.;
    for(i=0.; i < N; i++) {
        vec3 p = ro + t * rd;
        float d = map(p);
        if(d < MIN_DIST) {
            break;
        }
        t += d;
        if(t > 20.) {
            break;
        }
    }
    if(i == N) id = 0;
    return hit(t, 1.-i/N, id);
}

vec3 normal(vec3 p) {
    vec2 h = vec2(0.0001,0);
    return normalize(vec3(map(p+h.xyy) - map(p-h.xyy), 
                          map(p+h.yxy) - map(p-h.yxy), 
                          map(p+h.yyx) - map(p-h.yyx)));
}

vec3 brdf(vec3 N, vec3 L, vec3 V, vec3 diff, vec3 spec, float exponent) {
    vec3 R = reflect(L,N);
    vec3 H = normalize(L+V);
    return diff * clamp(dot(N, L), 0., 1.)
         + spec * pow(max(0., dot(R,V)), exponent);
}

vec3 shade(vec3 P, vec3 N, vec3 ro) {
    // N = N - mod(N, 0.15); // flat-shading
    vec3 V = normalize(P-ro);
    vec3 ambient = vec3(0.1,0.1,0.2);
    vec3 diffuse = vec3(.4, .4, .9);
    vec3 specular = vec3(.8, .8, 1.0);
    vec3 light1 = brdf(N, normalize(vec3(1,1,1) - P), V, diffuse, specular, 16.);
    vec3 light2 = brdf(N, normalize(vec3(-1,0,-0.5) - P), V, diffuse, specular, 16.);
    return ambient + light1 + light2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 uv = (2.*fragCoord-RES)/RES.y;
    vec3 ro = vec3(0,0.1,1.8);

    ro.xz *= rotate(iMouse.x/RES.x*3.14*2.);
    ro.yz *= rotate(-iMouse.y/RES.y*3.14*2.);

    vec3 ta = vec3(0,0,0);
    vec3 ww = normalize(ta-ro);
    vec3 uu = normalize(cross(ww, vec3(0,1,0)));
    vec3 vv = normalize(cross(uu,ww));
    vec3 rd = normalize(uv.x*uu + uv.y*vv + 2.2*ww);

    hit hit = trace(ro, rd);
    vec3 col = vec3(0);
    if(hit.t < 20.) {
        vec3 p = ro + hit.t*rd;
        vec3 n = normal(p);
        col = shade(p, n, ro) * hit.occlusion;
    }
    else {
        col = texture(iChannel0, rd).rgb;
    }

    fragColor = vec4(col, 1.);
}
