void mainImage( out vec4 color, in vec2 coord )
{
	vec4 a = texture(iChannel0,coord/iResolution.xy)*.02;
    color = .5+.5*cos(6.28*(.4+a.z+vec4(.0,.1,.2,.0)));
}

vec4 LOOKUP(vec2 u)
{
    vec2 f = fract(u)-.5;
    vec2 e = sign(f);
    vec2 d = 1./iResolution.xy;
    vec4 p1 = texture(iChannel0,(u+vec2(0.,0.)*e)*d);
    vec4 p2 = texture(iChannel0,(u+vec2(0.,1.)*e)*d);
    vec4 p3 = texture(iChannel0,(u+vec2(1.,0.)*e)*d);
    vec4 p4 = texture(iChannel0,(u+vec2(1.,1.)*e)*d);

    f = abs(f);
    vec2 v = 1.-f*2.;

    return  p1*(1.-min((0.+f.x)+(0.+f.y),1.))+
            p2*(1.-min((0.+f.x)+(1.-f.y),1.))+
            p3*(1.-min((1.-f.x)+(0.+f.y),1.))+
            (1.-max(v.x,v.y))*(p1+p2+p3+p4)/4.;
}
vec4 Field(vec2 position)
{
    vec2 velocityGuess = texture(iChannel0,position/iResolution.xy).xy;
    vec2 positionGuess = position - velocityGuess;
	return LOOKUP(positionGuess);
}
void mainImage( out vec4 Energy, in vec2 Me )
{
    vec4 C = Field(Me + vec2(0,0));//CENTER
    vec4 R = Field(Me + vec2(1,0));//RIGHT
    vec4 U = Field(Me + vec2(0,1));//UP
    vec4 L = Field(Me - vec2(1,0));//LEFT
    vec4 D = Field(Me - vec2(0,1));//DOWN

    C.xy += (-vec2(1,0)*(R.z-C.z)
             -vec2(0,1)*(U.z-C.z)
             +vec2(1,0)*(L.z-C.z)
             +vec2(0,1)*(D.z-C.z))/-4.;

    C.z = (C.z + R.z + U.z + L.z + D.z)/(5.+.05) +
          (+dot(-vec2(1,0),R.xy-C.xy)
           +dot(-vec2(0,1),U.xy-C.xy)
           +dot(+vec2(1,0),L.xy-C.xy)
           +dot(+vec2(0,1),D.xy-C.xy))/4.;

    if (iMouse.z > 0. && length(Me-iMouse.xy) < 100.)
    {
        C = vec4(0.);
    }
    //if(iFrame==0){
    	vec4 rnd = vec4(9392.346134,
                        8383.346134,
                        7374.346134,
                        6365.346134);
        rnd = sin(dot(Me+iTime,vec2(14.153,75.347)))*rnd;
    	C += (fract(rnd)-.5)*3.;
    //}

    Energy = C;
}
