void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv);
    fragColor.b += 0.1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord)/iResolution.xy;

    vec2 n = (fragCoord + vec2(0.0, 1.0))/iResolution.xy;
    vec2 s = (fragCoord + vec2(0.0, -1.0))/iResolution.xy;
    vec2 w = (fragCoord + vec2(-1.0, 0.0))/iResolution.xy;
    vec2 e = (fragCoord + vec2(1.0, 0.0))/iResolution.xy;

    vec4 nc = texture(iChannel0, n);
    vec4 sc = texture(iChannel0, s);
    vec4 wc = texture(iChannel0, w);
    vec4 ec = texture(iChannel0, e);

    if (iMouse.z > 0.0) {
	    if (round(fragCoord) == round(iMouse.xy)) {
	        fragColor = vec4(1.0, 0.01, 0.000001, 1.0)*100000.0;
	        return;
    	}
    }

    float t = 0.4 * iTime;

    float nr = texture(iChannel1, n + 0.3*abs(vec2(cos(t*1.1), sin(t*1.59)))).r;
    float sr = texture(iChannel1, s + 0.3*abs(vec2(cos(t*1.3), sin(t)*1.47))).r;
    float wr = texture(iChannel1, w + 0.3*abs(vec2(cos(t)*1.23, sin(t*1.43)))).r;
    float er = texture(iChannel1, e + 0.3*abs(vec2(cos(t)*1.37, sin(t*1.41)))).r;

    nr *= pow(nr, 1.2);
    sr *= pow(sr, 1.2);
    wr *= pow(wr, 1.2);
    er *= pow(er, 1.2);

    fragColor.rgb = (0.25 * (nc*nr + sc*sr + wc*wr + ec*er)*4.0).rgb;

    fragColor.r = clamp(fragColor.r, 0.0, 10000000.0);
    fragColor.g = clamp(fragColor.g, 0.0, 10000.0);
    fragColor.b = clamp(fragColor.b, 0.0, 100.0);

    fragColor.a = 1.0;
}
