#ifdef GL_ES
precision mediump float;
#endif

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.585);
}

float speed = 20.;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 sc_f = vec2(300,floor(iMouse.y/10.));

    float aux = (iMouse.x/iResolution.x);
	float prob = (aux+0.1)/(1.1);
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    st = st * sc_f;

    st.x += step(1., mod(st.y, 2.))*iTime*speed;
    st.x += (1.-step(1., mod(st.y, 2.)))*-iTime*speed;

    vec2 icoord = floor(st);
    vec2 fcoord = fract(st);

    vec3 color = vec3(0.);
    float rand1 = random(icoord)*random(icoord);
    float rand2 = pow(random(icoord),5.);
    color = step(1., mod(st.y, 2.))* vec3(floor(rand1+prob)) + (1.-step(1., mod(st.y, 2.)))*vec3(floor(rand2+prob));

    fragColor = vec4(color, 1.);
}
