                vec3 ligcN = cLight.c*cLight.s, ligoN = cLight.o;
                vec3 ligdirN = normalize(ligoN - posref);
                vec3 inligrefN = reflect(-ligdirN, norref);

                float dif = max(dot(norref, ligdirN), 0.);
                float spec = 0.5*pow(max(dot(viewdir, inligrefN), 0.0), gl);

                totalAmb += 0.1 *ligcN;
                totalLigCol += (dif + spec) * ligcN;

            }

            totalLigCol += totalAmb / vec3(LIGHT_AMOUNT);

            colref *= totalLigCol;
        }

