            Light( vec3(1.0,1.0,0.5), vec3(1000., 400., 100.), 1.4),
            Light( vec3(0.3,0.5,0.7), vec3(-100., 256, -100.), 0.2),
            Light( vec3(0.1), pos, 0.3)
        );

        vec3 viewdir = normalize(ro - pos);

        vec3 dcol = col;
        vec3 totalAmb = vec3(0.);
        vec3 totalLigCol = vec3(0.);

        //per-light rendering

        for(int i = 0; i < LIGHT_AMOUNT; i++) {
            Light cLight = lights[i];
            vec3 ligcN = cLight.c*cLight.s, ligoN = cLight.o;
            vec3 ligdirN = normalize(ligoN - pos);
            vec3 inligrefN = reflect(-ligdirN, nor);

            float dif = max(dot(nor, ligdirN), 0.) * calcSoftshadow(pos, ligdirN, 0.01, 0.3, 1);
            float spec = sSpec*pow(max(dot(viewdir, inligrefN), 0.0), gl);

            totalAmb += sAmb *ligcN;
