mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Reposition origin
    uv = -1.0+2.0*uv;
    // Adjusting aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    vec2 mo = iMouse.xy/iResolution.xy;

    // Get ray origin, ray direction

    vec3 ro = vec3( 5.*cos(0.1*iTime + 6.0*mo.x), 1.0 + 2.0*mo.y,5.*sin(0.1*iTime + 6.0*mo.x) );

    mat3 ca = setCamera( ro, vec3(0.0, 2.0, 0.), 0.01);
    vec3 rd = ca*normalize(vec3(uv, 2.0)); //normalization is unneccessary...

    DC dc = render(ro, rd);
    vec3 col = dc.col;

    //col = pow(col, vec3(1.0/2.2));
    //col *= sqrt(1.5-0.5*length(uv));
    //col *= 0.5 + 0.5*16.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y);
    vec2 bp = fragCoord.xy/iResolution.xy*2.-1.;
    float vgn1 = pow(smoothstep(0.0,.3,(bp.x + 1.)*(bp.y + 1.)*(bp.x - 1.)*(bp.y - 1.)),.5);
    float vgn2 = 1.-pow(dot(vec2(bp.x*.3, bp.y),bp),3.);
    col *= mix(vgn1,vgn2,.4)*.5+0.5;


    // Output to screen
    fragColor = vec4(col,1.0);
}
