vec4 conv2d(in ivec2 in_coord, in sampler2D b, in vec4[9 * 4] w, int upscale)
{
    vec4 x = vec4(0.0);
    for (int c = 0; c < 4; ++c)
    {
        float _x = 0.0;
    	for (int j = 0; j < 3; ++j)
        {
    		for (int i = 0; i < 3; ++i)
            {
                ivec2 coord = in_coord + ivec2(j-1, i-1);
            	_x += dot(w[9 * c + j + i * 3], texelFetch(b, coord / upscale, 0));
        	}
        }
        x[c] = _x;
    }
    return x;
}

float hash1( vec2 p )
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);  
}

// Taken from https://www.shadertoy.com/view/MlVSzw
const float ALPHA = 0.14;
const float INV_ALPHA = 1.0 / ALPHA;
const float PI = 3.1415926535;
const float K = 2.0 / (PI * ALPHA);

float inv_error_function(float x)
{
	float y = log(1.0 - x*x);
	float z = K + 0.5 * y;
	return sqrt(sqrt(z*z - y * INV_ALPHA) - z) * sign(x);
}
