#define PI 3.141592654
#define maxIteration 100
#define maxZoomLevel 20.0

//https://iquilezles.org/articles/palettes
vec3 palette(vec3 a, vec3 b, vec3 c, vec3 d, float t)
{
    return a + b*cos(2.0*PI*(c*t+d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 col = vec3(0.0, 0.0, 0.0);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec2 mouse = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);
    float zoomLevel = mouse.y * maxZoomLevel + 1.0;
    float offsetX = (mouse.x*zoomLevel) * 3.5 - 2.5;

    vec2 p = vec2(uv.x * 3.5 - 2.5 - offsetX, uv.y * 2.0 - 1.0) / zoomLevel;

    float x = 0.0;
    float y = 0.0;

    int iteration = 0;

    while (x*x + y*y <= 4.0 && iteration < maxIteration)
    {
        float xtemp = x*x - y*y + p.x;
        y = 2.0*x*y + p.y;
        x = xtemp;
        ++iteration;
    }

    col = palette(vec3(0.5), vec3(0.5), vec3(1.0), vec3(0.8, 0.9, 0.3), float(iteration) / float(maxIteration));


    // Output to screen
    fragColor = vec4(col.brg,1.0);
}
