/* 
	This attractor is based on https://www.shadertoy.com/view/Xsd3zn 
*/


#define AC vec2(1.5)
#define BD vec2(1.5)

/* 
	Increasing the number of iteriation make is slower but
	produce better results:
	https://en.wikipedia.org/wiki/Attractor
*/
#define ITER 300.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /* Initialize FragColor */
    fragColor = vec4(0.0);

    /* Align and zoom the origin to the center of the screen */
    vec2 uv = fragCoord / iResolution.xy - 0.5;
    uv *= 6.0;

    /* Simple time+mouse modifier */
    vec2 mod = iMouse.xy*0.01 + vec2(iTime,0);

    vec2 p = vec2(0.0);    

    /*
		Loop through:
			xn+1 = sin(A*yn) - cos(B*xn)
			yn+1 = sin(C*xn) - cos(D*yn)
	*/
    for (float i=1.0; i<2.0; i+=1.0/ITER){
        p = sin(mod+AC*p.yx) - cos(mod+BD*p*(fragCoord.x/fragCoord.y));
        fragColor.rgb += vec3(0.4, 0.2, 0.05) * 
						smoothstep( vec3(0.05, 0.08, 0.2)*i, vec3(0),
									vec3(distance(uv, p)));
    }
}
