#define colorRange 24.0

vec3 jodieReinhardTonemap(vec3 c)
{
    float l = dot(c, vec3(0.2126, 0.7152, 0.0722));
    vec3 tc = c / (c + 1.0);
    return mix(c / (l + 1.0), tc, tc);
}

vec3 bloomTile(float lod, vec2 offset, vec2 uv){
    return texture(iChannel1, uv * exp2(-lod) + offset).rgb;
}

vec3 getBloom(vec2 uv){

    vec3 blur = vec3(0.0);

    blur = pow(bloomTile(2., vec2(0.0,0.0), uv),vec3(2.2))       	   	+ blur;
    blur = pow(bloomTile(3., vec2(0.3,0.0), uv),vec3(2.2)) * 1.3        + blur;
    blur = pow(bloomTile(4., vec2(0.0,0.3), uv),vec3(2.2)) * 1.6        + blur;
    blur = pow(bloomTile(5., vec2(0.1,0.3), uv),vec3(2.2)) * 1.9 	   	+ blur;
    blur = pow(bloomTile(6., vec2(0.2,0.3), uv),vec3(2.2)) * 2.2 	   	+ blur;

    return blur * colorRange;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 base = texture(iChannel0,uv);

    vec3 color = pow(texture(iChannel0, uv).www * colorRange, vec3(2.2));
    color = pow(color, vec3(2.2));
    color += pow(getBloom(uv), vec3(2.2));

    float c_color = texture(iChannel0,uv).w;

    fragColor = vec4(base.rgb + 0.1*color,0.0);
}

float log_step()
{
    return 1.25;
}

const float extent = 40.0;

vec2 panning()
{
  return vec2(0.0); //extent * vec2(sin(iTime*0.1),cos(iTime*0.1));    
}

vec2 uv(vec2 p) 
{
    vec2 ctr = vec2(extent/2.0*iResolution.x/iResolution.y, extent/2.0) + panning();
    vec2 dt  = vec2(ctr-p);

    const float R = 0.4 * extent;    
    float r = length(dt);
    float a = atan(dt.y,dt.x) + (sin(iTime)*0.03) * r;
    return r * vec2(cos(a),sin(a));
}

vec2 d(vec2 p) 
{
    //return vec2(log_step()); // uncomment to reveal distortion    

    float delta = 0.1;
    vec2  dudx  = ( uv(p - vec2(delta,0.0)) - uv(p + vec2(delta,0.0)) ) * 0.5 / delta;
    vec2  dudy  = ( uv(p - vec2(0.0,delta)) - uv(p + vec2(0.0,delta)) ) * 0.5 / delta;
    float du    = log_step() / (0.00001 + length(vec2(dudx.x,dudy.x)));
    float dv    = log_step() / (0.00001 + length(vec2(dudx.y,dudy.y)));

    du = max(log_step(),du);
    dv = max(log_step(),dv);

    return vec2(du,dv);
}

