vec4 toGrayscale(in vec4 color)
{
   float average = (color.r + color.g + color.b) / 3.0;
   return vec4(average, average, average, 1.0);
}

vec4 colorize(in vec4 grayscale, in vec4 color)
{
    return (grayscale * color);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Change the direction value to modify the blur. Vec2(0,0) disable blur.
    vec2 direction = vec2(2.0,2.0);

    vec2 resolution = iResolution.xy;

    vec4 color = vec4(0.0);
    vec2 offset1 = vec2(1.3846153846) * direction;
    vec2 offset2 = vec2(3.2307692308) * direction;
    vec4 colorA = vec4(1.0,1.0,1.0, 1.0);       

    color += texture(iChannel0, uv) * 0.2270270270;
    color += texture(iChannel0, uv + (offset1 / resolution)) * 0.3162162162;
    color += texture(iChannel0, uv - (offset1 / resolution)) * 0.3162162162;
    color += texture(iChannel0, uv + (offset2 / resolution)) * 0.0702702703;
    color += texture(iChannel0, uv - (offset2 / resolution)) * 0.0702702703;

    // Convert to grayscale first:
  	vec4 grayscale = toGrayscale(color);

    // Then "colorize" by simply multiplying the grayscale
    // with the desired color.
    vec4 colorizedOutput = colorize(grayscale, colorA);

    fragColor = colorizedOutput;
}
