// explore the submerged columns, ruins of atlantis derived from 
#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define PI  3.14159

float map(vec3 p, mat2 m, mat2 n) {
	float k;
	p.xy *= m;
	p.yz *= n;
	for (int i = 0; i < 9; i++) {
		k = length(p.xy);
		p.x = k * (mod(atan(p.y, p.x), PI/4.) - PI/8.);				
		p.y = k - 2.759;		
		k = length(p.yz);
		p.y = k * (mod(atan(p.z, p.y), PI/9.5) - PI/19.);
		p.z = k - 10.0;
	}

	return dot(abs(p), normalize(vec3(-3.9,5,1.2))) - .886 ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse=iMouse.xy/iResolution.xy;
	vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
	vec3 rd = normalize(vec3(uv, 1));
	float v=-8.*mod(iTime/266.,PI*2.), u=mouse.x;
	vec3 p = vec3(0, 10.4+mouse.y*3., -6.);
	mat2 m = rot(u), n=rot(v);
	fragColor = vec4(0,0,.07,1);

	for (int i = 1; i < 60; i++) {
		float d = map(p, m, n);			
		p += rd * d;
		if (d < 0.001) {
			fragColor = vec4( 8,10,4.,1)/float(i);			
			break;
		}
	}
}
